function PayAccount_OnClick(obj)
{
	//-------------------------------------------------------------------------------
	// 함수명   : Button2_OnClick()
	// 설명     : 지불계좌 팝업
	// return 내용 : 없음
	//-------------------------------------------------------------------------------		
	var args  = '';
		
	args  = "fa_BankBrancd="     + quote(cob_BnkCd.Text);
	args += " fa_DealCd="        + quote(edt_DEAL_CD.Text);
	//args += " fa_PayBnkNum="     + quote(edt_PayAccountNum.Text);
	//args += " fa_DEPOSITOR_NM="  + quote(Edit21.Text);
	
	arr = gfn_Dialog("E40","E4501100010U",args,705,552,true,-1,-1);
	edt_PayAccountNum.Text = arr[0];
	
	// 2013.05.20. 이건민 - 권기철 과장님 추가요청 사항 (환불계좌 선택한 값 입력)
	edt_DEAL_CD.Text = arr[4];
	Edit21.Text = arr[5];
	
	fn_D1_CheckBnkCD();
	
	// 2013.05.23. 이건민 - 권기철 과장님 추가요청 (환불계좌 선택 시 환불코드가 없을때)
	if(edt_DEAL_CD.Text == "" || edt_DEAL_CD.Text == null)
	{
		cob_BnkCd.Text = '';
	}
	else
	{
		cob_BnkCd.Index = 0;
	}
	
}

//-------------------------------------------------------------------------------
// 함수명   : Button2_OnClick()
// 설명     : 지불계좌 팝업
// return 내용 : 없음
//-------------------------------------------------------------------------------	
function fn_D1_CheckBnkCD()
{			
	ds_BnkCd.ClearData();
	
	var strSvcID        = "findBnkCD";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_MoveingRepairAmountMgr-service";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_BnkCd=ds_BnkCd";
	var strArgument     = "findBnkCD=1";
		strArgument	   += " COM_ID="          + quote(ds_MoveDocuV.GetColumn(0, "DEAL_FLAG"      ));
		strArgument	   += " DEAL_CD="		  + quote(ds_MoveDocuV.GetColumn(0, "DEAL_CD"        ));
		strArgument	   += " PAY_ACCOUNT_NUM=" + quote(ds_MoveDocuV.GetColumn(0, "PAY_ACCOUNT_NUM"));
								 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );	
}

