// 그리드 
function ds_MoveAdj_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == 'PIPE_OPER_AMT' || strColumnID == 'SUM_PRICE')
	{
		var PIPE_OPER_AMT = ds_MoveAdj.GetColumn(nRow, "PIPE_OPER_AMT");
		var SUM_PRICE     = ds_MoveAdj.GetColumn(nRow, "SUM_PRICE");
		
		var str_SUM = toNumber(PIPE_OPER_AMT) + toNumber(SUM_PRICE);
		
		ds_MoveAdj.SetColumn(nRow, "TOTAL_AMT", str_SUM);
		
		edt_WORK_FIRM_ADJ_COST.Value = ds_MoveAdj.SumNF("TOTAL_AMT");
		
		//edt_WORK_FIRM_ADJ_COST.Value = 0;
		edt_REFUND_AMT.Value = toNumber(edt_WORK_FIRM_ADJ_COST.Value) - toNumber(edt_REQ_AMT.Value);		
		edt_REFUND_AMT_OnChanged();
	}
}

function Radio0_OnClick(obj)
{
	var str_First = Radio0.Value;

	if(str_First == 0){ // 선공사일 경우임
		Static38.Text = '미수금대체전표';
		Static39.Text = '';
		Calendar3.Visible = false;
		Edit12.Visible = false;
	}else{
		Static38.Text = '예수금대체전표';
		Static39.Text = '예수금대체_세금계산서';
		Calendar3.Visible = true;
		Edit12.Visible = true;
	}
}

function fn_E5_print()
{
	ds_ReportList.ClearData();
	
	var comId = gfn_GetGlobalValue("G_COM_ID");
	
	var vatBizplc      = ds_MoveDocuV.GetColumn(ds_MoveDocuV.row, "TXBILL_VAT_BIZPLC_2");  //발행순번      
	var publYm         = substr(ds_MoveDocuV.GetColumn(ds_MoveDocuV.row, "TXBILL_PUBL_YM_2"), 0, 6);  //발행년월          
	var publSeq        = ds_MoveDocuV.GetColumn(ds_MoveDocuV.row, "TXBILL_PUBL_SEQ_2"  );  //발행순번  
	
	var strTitle     ; 				// 출력물 제목
    var strPart      ;        		// 시스템 명 
	var strReport    ; 				// 출력물 ID 
	var strParameter ;      		// 파라미터구문 (단 ',' 로구분할것 )	
	var selChk ;
	var reportNm;
	var reportId;
	
	ds_ReportList.addRow();
	
	//각 파라미터 세팅 
	strTitle= vatBizplc+"-"+publYm +"-"+publSeq ;  	    // 출력물 제목
	
	//strTitle="미승인 전표출력";
	strPart   = "E50";           	// 시스템 명 
	strReport = "E5301010010P" ;    	// 출력물 ID 			
		            
	strParameter =  "COM_ID="     + comId     
	             + ",VAT_BIZPLC=" + vatBizplc      
				 + ",PUBL_YM="    + publYm 
				 + ",PUBL_SEQ="   + publSeq 
				 ;          			// 파라미터구문 (단 ',' 로구분할것 )
	
	ds_ReportList.SetColumn(ds_ReportList.row, "SYSTEM_ID",strPart);     	// 시스템 명 
	ds_ReportList.SetColumn(ds_ReportList.row, "REPORT_ID",strReport);  	// 출력물 ID 
	ds_ReportList.SetColumn(ds_ReportList.row, "REPORT_ARGS",strParameter); // 파라미터구문 
	ds_ReportList.SetColumn(ds_ReportList.row, "REPORT_TITLE",strTitle);   	// 출력물 제목

	gfn_MultiReportPopup("세금계산서출력",ds_ReportList, "true");
}

