	{
		return;
	}
	// alert(ds_MoveDocu.GetColumn(ds_MoveDocu.Row, "MOVE_REPAIR_NUM"));
	// alert(ds_MoveAdj.GetColumn(ds_MoveAdj.row, "WORK_NUM"));
	// alert(ds_MoveDocu.GetColumn(ds_MoveDocu.Row, "PIPE_OPER_PRICE_YEAR"));
	var strTitle    = "도관운영비 및 가스방출비 산출내역서" ;  		// 출력물 제목
    var strPart     = "D10" ;         	    		  				// 시스템 명 
	var strReport   = "D1702010010P" ;  	    	 				// 출력물 ID 
	var strParameter= "MOVE_REPAIR_NUM="     + replace(ds_MoveDocu.GetColumn(ds_MoveDocu.Row, "MOVE_REPAIR_NUM"),"%$","")
				    + ",BASE_YEAR="		     + ds_MoveDocu.GetColumn(ds_MoveDocu.Row, "PIPE_OPER_PRICE_YEAR")
				    + ",PIPE_OPER_AMT="      + ds_MoveDocuV.GetColumn(0, "PIPE_OPER_AMT")
				    ;
				   
	//공통 레포트 팝업 함수 호출 
	gfn_ReportPopup(strTitle,strPart,strReport,strParameter);	
}

