 * 함수명: fn_CommonAddRow()
   설명: 탭별  내용를 신규 추가 하는 모드로 변경 하는 함수
   return 내용: 없음
===============================================================================*/ 
function fn_CommonAddRow()
{
   	tab_FaciReg.GetItem(tab_FaciReg.TabIndex).fn_CommonAddRow();
}

//---------------------------------------------------------
//	공통 버튼 클릭 - 저장
//---------------------------------------------------------
function fn_CommonSave()
{
	var result = tab_FaciReg.GetItem(tab_FaciReg.TabIndex).fn_D1_Save();
	
	if(result > 0){
		fn_D1_Find();
	}
}

//---------------------------------------------------------
//	공통 버튼 클릭 - 조회
//---------------------------------------------------------
function fn_CommonSearch()
{	
	fn_D1_Find();
	tab_FaciReg_OnChanged();
}


