//---------------------------------------------------------
//	데이터 클릭시 TAB  조회
//---------------------------------------------------------
function Guid0_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	FaciNumRow = nRow;
	FaciNum    = ds_GvRmList.GetColumn(nRow, "FACI_NUM"    );
	ValueType  = ds_GvRmList.GetColumn(nRow, "LEASE_STS_CD");

	tab_FaciReg.GetItem(tab_FaciReg.TabIndex).fn_D1_find(nRow, ds_GvRmList.GetColumn(nRow, "FACI_NUM"), ds_GvRmList.GetColumn(nRow, "LEASE_STS_CD"));
	
	// 정압실별 정압기번호를 조회하여 온다 
	fn_D1_GovFind();
	
	// GIS
	if(nCell == 17){
			args  = "BUNGI='04'";
			args += " FACI_NUM=" + ds_GvRmList.GetColumn(nRow, "FACI_NUM");
						
			gfn_Dialog("D10","D1402060011U",args,705,552,true,-1,-1);	
	}
	
	// 장표(멀티)
	if(nCell == 14){
		btn_MultiReport_OnClick();
	}
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_GovFind()
//   설명  :  사유지 점용료 협의사항를 조회한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Find()
{	
    var strSvcID        = "";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_RentalDiscuRmkRegisMgr-service" 
						+ "&BRANCH_CD="     + cob_ObjectFlag.Value
						+ "&ZIP_NO1="       + cob_CountyFlag.Value
					    + "&GOV_ROOM_NM="   + edt_GovRoomName.Text
					    + "&LEASE_STS_CD="  + cob_LeaseState.Value						
					    + "&LEASE_CLASS="   + cob_LeaseClass.Value								    										    
						+ "&PAY_YN="        + cob_PayYn.Value		
						+ "&PROC_STS="      + decode(cob_State.Text, '전체', '', cob_State.Text)
						;
						
	var strInDatasets   = "";
	var strOutDatasets  = "ds_GvRmList=ds_GvRmList ds_Excel=ds_Excel";
	//var strArgument     = "";
	var strArgument = "find";
	
	/*
		var NCob = ds_Combo.GetColumn(cob_State.Index, "VALUE");
		
		if(NCob == '01'){
			strArgument = "find";
		}else if(NCob == '02'){
			strArgument = "find1";
		}else if(NCob == '03'){
			strArgument = "find2";
		}else if(NCob == '04'){
			strArgument = "find3";
		}else if(NCob == '05'){
			strArgument = "find4";
		}
	*/
		 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   	
	//----  등록 카운트 
	var j=0;
	
	for(var i=0; i < ds_GvRmList.GetRowCount();i++)
	{
		  j=j+1;
	}	
	
	Static71.Value = LPAD(j, '0', 3);
	
	Guid0_OnCellClick();
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Find()
//   설명  :  사유지 정압실별 정압기 정보를 조회하여 온다. 
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_GovFind()
{	
    var strSvcID        = "";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_RentalDiscuRmkRegisMgr-service" 
						+ "&FACI_NUM=" + ds_GvRmList.GetColumn(ds_GvRmList.currow, "FACI_NUM")
						;

	var strInDatasets   = "";
	var strOutDatasets  = "ds_GovList=ds_GovList";
	var strArgument     = "findGov";
	 	 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}

//-------------------------------------------------------------------------------
// 함수명: fn_CommonExcelExport()
// 설명: 엑셀 button 실행, 조회된데이터를 엑셀형식으로변환한다
// arguments	없음
// return  없음
//-------------------------------------------------------------------------------
function fn_CommonExcelExport()
{	      
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "지사 : "     + RPad(cob_ObjectFlag.Text," ",20);
    SearchCondition = SearchCondition + "시/구 : "    + RPad(cob_CountyFlag.Text," ",20);
    SearchCondition = SearchCondition + "진행상태 : " + RPad(cob_State.Text     ," ",20);
    
    gfn_ExportExcel("사유지점용료 협의 사항등록", 5, SearchCondition, "grd_excel");  
}

