//-------------------------------------------------------------------------------
// 함수명: btn_MultiReport_OnClick()
// 설명: 공통 레포트 팝업 함수를 호출한다(멀티 보고서), 멀티 보고서 파라미터를 전달을
//       위한 dataset을 사용한다
// input parameter: 
// output parameter: 
// return 내용: 없음
//-------------------------------------------------------------------------------
function btn_MultiReport_OnClick(obj)
{
	ds_ReportList.ClearData();	
	var nCount = ds_GovList.GetRowCount();
		
	// 함수 호출용 파라미터를 만든다
	var strTitle     ;				// 출력물 제목
    var strPart      ;         		// 시스템 명 
	var strReport    ; 				// 출력물 ID 
	var strParameter ;      		// 파라미터구문 (단 ',' 로구분할것 )	

		for(var i=0; i<nCount; i++){
		
			//행추가
			ds_ReportList.addRow();
			
			// 함수 호출용 파라미터를 설정한다
			var strTitle 	 ="정압기 시설물 카드 조회"; 
			var strPart 	 ="D10";           
			var strReport    ="D1202010037P"; 
			var strParameter ="FACI_NUM="    + ds_GovList.GetColumn(i, "FACI_NUM")
							 +",GOV_NUM="    + ds_GovList.GetColumn(i, "GOV_NUM")
							 ;
							 			
			ds_ReportList.SetColumn(i, "SYSTEM_ID"   ,strPart     ); // 시스템 명 
			ds_ReportList.SetColumn(i, "REPORT_ID"   ,strReport   ); // 출력물 ID 
			ds_ReportList.SetColumn(i, "REPORT_ARGS" ,strParameter); // 파라미터구문 
			ds_ReportList.SetColumn(i, "REPORT_TITLE",strTitle    ); // 출력물 제목
		}
	
	// 데이터셋 생성후 OZ 멀티 출력 함수 호출, concatpage 사용여부를 true, false로 결정한다(공백("")이라도 넣어야 한다)
	gfn_MultiReportPopup("정압기 시설물 카드 조회",ds_ReportList, "true");
}

//---------------------------------------
//  코드정보를 추충하는 부분(전체항목 항목 추가, 사유지, 학교만 필요함)  
//  Parameter : Input : 코드ID, Dataset Name
//---------------------------------------
function gfn_D1_All_SetComboBox_Comcode1(strCodeGrp,objComboDs,val, val1, val2)
{
		var strSvcID = strCodeGrp;
		var strPart = "A10";
		var strURL = "handle.do?ServiceName=A1101010021U-service&find1=1&CODE_ID="+strCodeGrp;
		var strInDatasets  = "";
		var strOutDatasets  = objComboDs+"=ds_CodeItems";
		var strArgument  = "";

	    gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets   
				   , strArgument
			   );
		    
		if (  ErrorCode<0 )   
		{
			return;
		}else{
		    
		    eval(objComboDs).deleterow(eval(objComboDs).findrow("CODE_ITEM", "20"));
		    eval(objComboDs).deleterow(eval(objComboDs).findrow("CODE_ITEM", "30"));
		    eval(objComboDs).deleterow(eval(objComboDs).findrow("CODE_ITEM", "50"));
		    
		    var cnt = eval(objComboDs).rowcount();
		    eval(objComboDs).addrow();
		    eval(objComboDs).setcolumn(cnt, "CODE_ID"   , val   );
		    eval(objComboDs).setcolumn(cnt, "CODE_ITEM" , val1  );
		    eval(objComboDs).setcolumn(cnt, "ITEM_KNAME", val2  );
			
			return;
		}
}

function tab_FaciReg_OnChanged(obj,nOldIndex,nNewindex)
{
	var NTab = tab_FaciReg.TabIndex;

	if(NTab == '1' || NTab == '3'){
		Image5.Visible    = true ;
		cob_PayYn.Visible = true ;
	}else{
		Image5.Visible    = false;
		cob_PayYn.Visible = false;			
		cob_PayYn.Index = 4;
	}

	tab_FaciReg.GetItem(tab_FaciReg.TabIndex).fn_D1_Find(ds_GvRmList.row, ds_GvRmList.GetColumn(ds_GvRmList.row, "FACI_NUM"));
		
	//----  등록 카운트 
	var j=0;
	
	for(var i=0; i < ds_GvRmList.GetRowCount();i++)
	{
		  j=j+1;
	}	
	
	Static71.Value = LPAD(j, '0', 3);
}
