//---------------------------------------------------------
//	공통 버튼 클릭 - 저장
//---------------------------------------------------------
function fn_CommonSave()
{
	fn_D1_Save();
}

function fn_CommonAddRow()
{	
	if(ds_FpNmInst.GetRowCount() == 0){
		
		if(parent.ValueType == '10' || parent.ValueType == '30'){
			alert("무상 또는 미체결 대상으로 시설물정보에서 내용을 수정후 사용가능합니다. ");
			return;
		}
		
		var i = ds_FpNmInst.AddRow();
		ds_FpNmInst.SetColumn(i, "PAY_NM_TEL_DDD", '02');
		ds_FpNmInst.SetColumn(i, "TXBILL_PUBL_YN", 'N' );
		ds_FpNmInst.SetColumn(i, "EVIDENCE_YN"   , 'N' );
	}
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_SAVE
//   설명  : 사유지 점용료 협의사항를 등록/업데이트한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Save()
{	
	if(parent.ValueType == '10' || parent.ValueType == '30'){
		alert("무상 또는 미체결 대상으로 시설물정보에서 내용을 수정후 사용가능합니다. ");
		return false;
	}
	
	if(ds_FpNmInst.GetRowType(0) == "normal"){
		alert("등록 또는 변경내용이 존재하지 않습니다. ");
		return false;
	}	
	
	if(ds_BnkCd.Text == '') {
		alert("은행명을 입력하세요. ");
		return;
	}
	
	if(edt_AccountNum.Text == '') {
		alert("은행명을 입력하세요. ");
		return false;
	}
		
	if(edt_DepositorNm.Text == '') {
		alert("예금주를 입력하세요. ");
		return false;
	}

	if(Edit0.Text == '' || Edit1.Text == '') {
		alert("협약자연락처를 입력하세요. ");
		return false;
	}
	
	if(edt_PayYear.Text == '') {
		alert("사업자(주민)번호를 입력하세요. ");
		return false;
	}
		
	if(Calendar0.Value == '') {
		alert("허가기간을 지정하세요. ");
		return false;
	}
	
	if(cob_EvidenceYn.Value == '') {
		alert("증빙구분을 지정하세요. ");
		return false;
	}
	
	if(Combo0.Value == '') {
		alert("세금계산서 발행여부를 지정하세요.");
		return false;
	}

	var USER_ID    = gfn_GetGlobalValue("G_USER_NO"  ); // SESSION ID
	var USER_IP    = gfn_GetGlobalValue("G_IPADDRESS"); // SESSION IP

	// DB에 입력시킬 다음 값들을  셋팅 시켜 준다.
	ds_FpNmInst.SetColumn(0, "FACI_NUM"   , parent.ds_GvRmList.GetColumn(nRowNum, "FACI_NUM" ));
	ds_FpNmInst.SetColumn(0, "BRANCH_CD"  , parent.ds_GvRmList.GetColumn(nRowNum, "BRANCH_CD"));
	
	ds_FpNmInst.SetColumn(0, "UPD_EMPID"  , USER_ID       );
	ds_FpNmInst.SetColumn(0, "UPD_IP"     , USER_IP       );	
	ds_FpNmInst.SetColumn(0, "CRT_EMPID"  , USER_ID       );
	ds_FpNmInst.SetColumn(0, "CRT_IP"     , USER_IP       );
	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
		
	var strSvcID         = "";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1_RentalDiscuRmkRegisMgr-service" ;					
	var strInDatasets    = "ds_FpNmInst=ds_FpNmInst:u";
	var strOutDatasets   = "";		
	var strArgument      = "save_Tab02";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );			   
	
	if(ErrorCode<0)
	{
		return false;
	}
	
	alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
	return true;
}

//---------------------------------------------------------
//	공통 버튼 클릭 - 조회
//---------------------------------------------------------
function fn_CommonSearch()
{
	fn_D1_Find(nRowNum, nValueNum, ValueType);
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Find()
//   설명  :  사유지 점용료 협의사항를 조회한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Find(nRow, value, value1)
{	
	nRowNum   = nRow;
	nValueNum = value;
	ValueType = value1;
	
    var strSvcID        = "";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_RentalDiscuRmkRegisMgr-service" 
						+ "&FACI_NUM=" + value
						;
	var strInDatasets   = "";
	
	// 협의정보 DataSet
	var strOutDatasets  = "ds_GvRmInst=ds_GvRmInst ds_GvRmInst1=ds_GvRmInst1 ds_FpNmInst=ds_FpNmInst";
	var strArgument     = "view_Tab02";
	 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
		
	if(ds_GvRmInst.GetColumn(0, "DOCU_RECEIVE_YMD") == '' || ds_GvRmInst.GetColumn(0, "DOCU_RECEIVE_YMD") == null){
		Radio.Value = 1;
		Radio_OnClick();
	}else{
		Radio.Value = 0;
		Radio_OnClick();
	}			   
	
	btn_BnkBranchCd.Enable = true;
	Button4.Enable         = true;
	Button23.Enable        = true;
	btn_BizCd.Enable       = true;
			   
	// 값이 없을 경우 Insert 조회 기록이 있을 경우 Update 처리
	if(ds_FpNmInst.RowCount() == 0)
	{
		if(parent.ValueType == '10' || parent.ValueType == '30'){
			//alert("무상 또는 미체결 대상으로 시설물정보에서 내용을 수정후 사용가능합니다. ");			
			//ds_GvRmInst1.ClearData();
			ds_FpNmInst.ClearData();
			
			btn_BnkBranchCd.Enable = false;
			Button4.Enable         = false;
			Button23.Enable        = false;
			btn_BizCd.Enable       = false;
		}
	}
	
	fn_d1_FileSearch();
}

//-------------------------------------------------------------------------------
// 함수명: fn_d1_FileSearch()
//   설명: 첨부파일 조회
// arguments   
//          obj: 사용된 객체를 전달 한다.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_d1_FileSearch()
{
	var strSvcID = "";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_RentalDiscuRmkRegisMgr-service&findFile=1"
	           + "&FACI_NUM="           + ds_GvRmInst.GetColumn(0, "FACI_NUM")
	           ;
	           	
	var strInDatasets   = "";
	var strOutDatasets  = "";
	    strOutDatasets += "ds_FileList=ds_FileList";
	var strArgument  = "";
		
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ErrorCode < 0)
	{
		return;
	}
	
	edtFileList.text = "";
	
	for(var i=0; i<ds_FileList.RowCount(); i++)
	{
		edtFileList.text += ds_FileList.GetColumn(i,"ATTACH_FILE_NM");
			if(ds_FileList.RowCount()-1 != i) edtFileList.text += "/";
		}	
}

//-------------------------------------------------------------------------------
// 함수명: Button0_OnClick()
//   설명: 첨부파일 popup화면 로딩
// arguments   
//          obj: 사용된 객체를 전달 한다.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function Button0_OnClick(obj)
{
	if(ds_GvRmInst.GetColumn(0, "FACI_NUM") == null || ds_GvRmInst.GetColumn(0, "FACI_NUM") == ''){
		alert("하나의 정압실을 선택하세요. ");
		return;
	}
	
	var arg = "fa_Url=handle.do?ServiceName=D1_RentalDiscuRmkRegisMgr-service&FACI_NUM="+ ds_GvRmInst.GetColumn(0, "FACI_NUM") +"&ATTACH_FILE_FLAG=" + ds_GvRmInst.GetColumn(0, "MOVE_REPAIR_TYPE") + " fa_Part=D10";
	
	var arrList = gfn_Dialog("D10","D1101010018U",arg,450,280,true,-1,-1);
	
	edtFileList.text = "";
	
	for(var i=0; i<arrList.length; i++)
	{
		edtFileList.text += arrList[i];
		if(arrList.length-1 != i) edtFileList.text += "/";
	}
}

