// 데이터셋 초기화
function fn_D1_ClearData(){
	ds_FileList.ClearData();
	ds_FpNmInst.ClearData();
	ds_GvRmInst.ClearData();
	ds_GvRmInst1.ClearData();
}

// 부가세와 세금계산서 동일 여부
function Combo0_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(ds_FpNmInst.GetColumn(0, "VAT_YN") == '' || ds_FpNmInst.GetColumn(0, "VAT_YN") == null){
		return;
	}
	
	if(Combo0.Value != ds_FpNmInst.GetColumn(0, "VAT_YN")){
		var result = confirm("부가세적용여부와 동일하지 않습니다. 계속하시겠습니까? ");
		
		if(result == '0'){
			Combo0.Value = ds_FpNmInst.GetColumn(0, "TXBILL_PUBL_YN1");
			return;
		}
	}
}

// 거래처 팝업
function btn_BizCd(obj)//거래처코드
{
 //-------------------------------------------------------------------------------
 // 함수명   : btn_BizCd()
 // 설명     : 부가세종류코드 팝업
 // return 내용 : 없음
 //-------------------------------------------------------------------------------
	 var retValue=gfn_E5_CodePopup(edt_Depositor.Text,"","DEAL_CD"); 
	
	 if(retValue != null && length(retValue)!=0){
		 edt_Depositor  .Text=retValue[0]; 
		 edt_DepositorNm.Text=retValue[1];
		 edt_DealFlag   .Text=retValue[14]; 
	 	 
		var result = subStr(retValue[0], 0, 3) + "-" + subStr(retValue[0], 3, 2) + "-" + subStr(retValue[0], 5, 5);
	 	 
		edt_PayYear.Text = result;
	}
}

function btn_BnkBranchCd(obj)//은행지점코드
{
	//-------------------------------------------------------------------------------
	// 함수명   : gfn_E5_CodePopup(code,codeNm,codeKind)
	// 설명     : 팝업을 호출한다
	// return 내용 : 팝업에서 선택된값
	//-------------------------------------------------------------------------------
	 var retValue=gfn_E4_CodePopup("",ds_BnkCd.Text,"","BNK_CD"); //은행지점코드(조회조건용)

	if(retValue != null && length(retValue)!=0){
		ds_BnkCd.Text=retValue[0];
		ds_BnkNm.Text=retValue[1];
	}
}

