var SaveType = '';

function D1802010010F_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	fn_Init();
}                                                                 

//---------------------------------------------------------
//	초기 설정
//---------------------------------------------------------
function fn_Init()
{
	//-----------------------------------------
	//  콤보박스의 내용을 셋팅한다.
	// 지사코드, 영업구코드
	//-----------------------------------------
	gfn_D1_All_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_ObjectFlag"));	
	//-----------------------------------------
	//  콤보박스의 초기 설정 : 기준년도, 대상구분, 점용시설 순서
	//----------------------------------------
	cob_ObjectFlag.Index = 0;	
	//-----------------------------------------
	//  콤보박스의 초기 설정 : 기준년도, 지사
	//-----------------------------------------
	cob_BaseYear.Value   = Substr(Today(),0,4)+"년";
	
	cob_BranchCd_OnChanged();
}

/********************************************************************************
* 기      능   :  지사별 구를 조회하여 콤보박스에 셋팅
********************************************************************************/
function cob_BranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {

    // 지사별 구 콤보 셋팅 시
    fn_SetComboBox_Comcode("1", cob_ObjectFlag.Value, fn_setDsObject_Comcode("1", "cob_CountyFlag"));
    cob_CountyFlag.Index = 6;
    
    SaveType = '';
}

/*===============================================================================
 * 공통 div_ComHead 에서 버튼 이벤트
		추가	fn_CommonAddRow
		삭제	fn_CommonDeleteRow
		저장	fn_CommonSave
		조회	fn_CommonSearch
		엑셀	fn_CommonExcelExport
		인쇄	fn_CommonPrint
		닫기	공통에서 처리
===============================================================================*/ 
//-------------------------------------------------------------------------------
// 함수명: fn_CommonSearch()
// 설명: 조회 button 실행, 데이터를 조회 한다
// arguments	없음
// return  없음
//-------------------------------------------------------------------------------
function fn_CommonSearch(){
	
	Grid0.BindDataset = "";
	Grid1.BindDataset = "";
	Grid2.BindDataset = "";
	
	// 기준년도, 관경, 구도
	var result  = fn_D1_Count(); // 구도
	
	fn_D1_find (result);  //구도
	fn_D1_find1(result); //시도
	fn_D1_find2();		  //합계
		
	Grid0.BindDataset = "ds_Copy_a";
	Grid1.BindDataset = "ds_Copy_b";
	Grid2.BindDataset = "ds_Copy_c";
	
	SaveType = 'select';
}

function fn_CommonDeleteRow(){
	
	var result = Confirm("데이터를 삭제하시겠습니까? ");
		
	if(result == '0'){
			return;
	}
	
	// 등록여부체크
	var result_save = fn_D1_Count();
	
	if(result_save == '0'){
		return;
	}
	
	ds_Delete.ClearData();
	ds_Delete.AddRow();
	
	ds_Delete.SetColumn(0, "BASE_YEAR", ds_Copy_a.GetColumn(0, "BASE_YEAR"));
	ds_Delete.SetColumn(0, "BRANCH_CD", ds_Copy_a.GetColumn(0, "BRANCH_CD"));
	ds_Delete.SetColumn(0, "ZIP_NO1"  , ds_Copy_a.GetColumn(0, "ZIP_NO1"));
	
	ds_Delete.UpdateControl = false;
	ds_Delete.SetRowType(0, "delete");
				
	var strSvcID         = "";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1_RoadLeaseObjRegisMgr-service" ;
	var strInDatasets    = "ds_delete=ds_delete:u";
	var strOutDatasets   = "";		
	var strArgument      = "delete_all";	
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				);			   

	alert("데이터가 정상적으로 삭제되었습니다. ");
	fn_CommonSearch();
}

//-------------------------------------------------------------------------------
// 함수명: fn_CommonSave()
// 설명: 저장 button 실행, 데이터를 저장 한다
// arguments	없음
// return  없음
//-------------------------------------------------------------------------------
function fn_CommonSave(){

	var str_BaseYear = cob_BaseYear.Value;
	var str_BranchCd = cob_ObjectFlag.Value;
	var str_ZipNo	 = cob_CountyFlag.Value;

	if(length(str_BaseYear) != 4){
		alert("기준년도를 정확히 선택하세요. ");
		return;
	}
	
	if(str_BranchCd == '' || str_BranchCd == null){
		alert("지사를 선택하세요. ");
		return;
	}
	
	if(str_ZipNo == '' || str_ZipNo == null){
		alert("시/구를 선택하세요. ");
		return;
	}
	
	if(SaveType != 'select'){
		alert("기준년도, 지사, 시/구 정보가 변경되었습니다. 조회 후 다시 등록하세요. ");
		return;
	}
	
	fn_D1_save (); // 구도
	
	fn_CommonSearch();
}

