}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Find()
//   설명  :  도로점용 시설물 설치현황 조회
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_find(result)
{			    
    var strSvcId       = "";
    var strPart        = "";
    var strURL         = "";
    var strInDatasets  = "";
    var strOutDatasets = "";
    var strArgument	   = "";
    
    if(result  == '0'){
			     strSvcID        = "";
			     strPart         = "D10";	
			     strURL          = "handle.do?ServiceName=D1_RoadLeaseObjRegisMgr-service"
								 + "&BASE_YEAR=" + cob_BaseYear  .Value
								 + "&BRANCH_NM=" + cob_ObjectFlag.Text
								 + "&GU_NM="     + decode(cob_CountyFlag.Text, '전체', '', cob_CountyFlag.Text)
								 ;
			     strInDatasets   = "";
			     strOutDatasets  = "ds_BeforeLeng=ds_BeforeLeng ds_CurrentLeng=ds_CurrentLeng ds_BeforeSame=ds_BeforeSame ds_DeleteLeng=ds_DeleteLeng ds_NewLeng=ds_NewLeng ds_DiffLeng=ds_DiffLeng";
			     strArgument     = "find_Lease";		
	}else{
		         strSvcID        = "";
			     strPart         = "D10";	
			     strURL          = "handle.do?ServiceName=D1_RoadLeaseObjRegisMgr-service"
								 + "&BASE_YEAR=" + cob_BaseYear  .Value
								 + "&BRANCH_CD=" + cob_ObjectFlag.Value
								 + "&ZIP_NO1="   + cob_CountyFlag.Value
								 ;
			     strInDatasets   = "";
			     strOutDatasets  = "ds_Copy_a=ds_Copy_a";
			     strArgument     = "find1";
	}  
	 	 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );	   

	if(result  == '0'){	

		ds_Copy_a.AddColumn("BEFORE_PRICE");
		ds_Copy_a.AddColumn("REPORT_LENG" );	
		ds_Copy_a.AddColumn("EXCLUDE_PIPE"); 
		ds_Copy_a.AddColumn("PRICE"       ); 
		ds_Copy_a.AddColumn("RMK"		  );
		
		for(var i=0; i<ds_Copy_a.GetRowCount(); i++){
		  
		 ds_Copy_a.SetColumn(i, "BEFORE_LENG"       , ds_BeforeLeng .GetColumn(i, "BEFORE_LENG" 	 ));
		 ds_Copy_a.SetColumn(i, "BEFORE_PRICE", 0);
		 ds_Copy_a.SetColumn(i, "PRICE"       , 0);
		 ds_Copy_a.SetColumn(i, "CURRENT_LENG"	    , ds_CurrentLeng.GetColumn(i, "CURRENT_LENG"	 ));
		 ds_Copy_a.SetColumn(i, "BEFORE_SAME" 		, ds_BeforeSame .GetColumn(i, "BEFORE_SAME" 	 ));
		 ds_Copy_a.SetColumn(i, "DELETE_LENG" 		, ds_DeleteLeng .GetColumn(i, "DELETE_LENG" 	 ));
		 ds_Copy_a.SetColumn(i, "NEW_LENG"    		, ds_NewLeng    .GetColumn(i, "NEW_LENG"    	 ));
		 ds_Copy_a.SetColumn(i, "BEFORE_DIFF_LENG"  , ds_DiffLeng   .GetColumn(i, "BEFORE_DIFF_LENG" ));
		 ds_Copy_a.SetColumn(i, "CURRENT_DIFF_LENG" , ds_DiffLeng   .GetColumn(i, "CURRENT_DIFF_LENG"));
		 ds_Copy_a.SetColumn(i, "DIFF_LENG"			, toNumber(ds_DiffLeng   .GetColumn(i, "BEFORE_DIFF_LENG"  ))
													- toNumber(ds_DiffLeng   .GetColumn(i, "CURRENT_DIFF_LENG" ))
							);		 					
	 }
	}
}

