			 ds_Copy_b.SetColumn(i, "DELETE_LENG" 		, ds_DeleteLeng_0.GetColumn(i, "DELETE_LENG" 	 ));
			 ds_Copy_b.SetColumn(i, "NEW_LENG"    		, ds_NewLeng_0.GetColumn(i, "NEW_LENG"    	 ));
			 ds_Copy_b.SetColumn(i, "BEFORE_DIFF_LENG"  , ds_DiffLeng_0.GetColumn(i, "BEFORE_DIFF_LENG" ));
			 ds_Copy_b.SetColumn(i, "CURRENT_DIFF_LENG" , ds_DiffLeng_0.GetColumn(i, "CURRENT_DIFF_LENG"));
			 ds_Copy_b.SetColumn(i, "DIFF_LENG"			, toNumber(ds_DiffLeng_0.GetColumn(i, "BEFORE_DIFF_LENG"  ))
														- toNumber(ds_DiffLeng_0.GetColumn(i, "CURRENT_DIFF_LENG" ))
								);
			}
		}
	}
}

function fn_D1_find2(){
	
	ds_Copy_c.Copy(ds_Copy_a);
		 
	for(var i=0; i<ds_Copy_a.GetRowCount(); i++){	
		 ds_Copy_c.SetColumn(i, "ZIP_NM", "합계");
		 ds_Copy_c.SetColumn(i, "BEFORE_PRICE"      , toNumber(ds_Copy_a.GetColumn(i, "BEFORE_PRICE"     )) + toNumber(ds_Copy_b.GetColumn(i, "BEFORE_PRICE"    )));
		 ds_Copy_c.SetColumn(i, "PRICE"       	    , toNumber(ds_Copy_a.GetColumn(i, "PRICE"            )) + toNumber(ds_Copy_b.GetColumn(i, "PRICE"           )));
		 ds_Copy_c.SetColumn(i, "BEFORE_LENG"       , toNumber(ds_Copy_a.GetColumn(i, "BEFORE_LENG"      )) + toNumber(ds_Copy_b.GetColumn(i, "BEFORE_LENG"     )));
		 ds_Copy_c.SetColumn(i, "CURRENT_LENG"	    , toNumber(ds_Copy_a.GetColumn(i, "CURRENT_LENG"	 )) + toNumber(ds_Copy_b.GetColumn(i, "CURRENT_LENG"	)));
		 ds_Copy_c.SetColumn(i, "BEFORE_SAME" 		, toNumber(ds_Copy_a.GetColumn(i, "BEFORE_SAME" 	 )) + toNumber(ds_Copy_b.GetColumn(i, "BEFORE_SAME" 	)));
		 ds_Copy_c.SetColumn(i, "DELETE_LENG" 		, toNumber(ds_Copy_a.GetColumn(i, "DELETE_LENG" 	 )) + toNumber(ds_Copy_b.GetColumn(i, "DELETE_LENG" 	)));
		 ds_Copy_c.SetColumn(i, "NEW_LENG"    		, toNumber(ds_Copy_a.GetColumn(i, "NEW_LENG"     	 )) + toNumber(ds_Copy_b.GetColumn(i, "NEW_LENG"     	)));
		 ds_Copy_c.SetColumn(i, "BEFORE_DIFF_LENG"  , toNumber(ds_Copy_a.GetColumn(i, "BEFORE_DIFF_LENG" )) + toNumber(ds_Copy_b.GetColumn(i, "BEFORE_DIFF_LENG")));
		 ds_Copy_c.SetColumn(i, "DIFF_LENG"         , toNumber(ds_Copy_a.GetColumn(i, "DIFF_LENG"        )) + toNumber(ds_Copy_b.GetColumn(i, "DIFF_LENG"       )));
	 }

}

//------------------------------------------------------------------------------- 
//   함수명 : fn_D1_SAVE
//   설명  : 도로점용 시설물 설치현황 조회
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 	
function fn_D1_save(){
	
	// 기준년도, 관경, 구도
	var result  =  fn_D1_Count(); // 구도
		
	var USER_ID = gfn_GetGlobalValue("G_USER_NO")  ;   // SESSION ID
	var USER_IP = gfn_GetGlobalValue("G_IPADDRESS");   // SESSION IP
	
	ds_Copy_a.AddColumn("UPD_EMPID");
	ds_Copy_a.AddColumn("UPD_IP"   );
	ds_Copy_a.AddColumn("CRT_EMPID");
	ds_Copy_a.AddColumn("CRT_IP"   );	
	
	ds_Copy_b.AddColumn("UPD_EMPID");
	ds_Copy_b.AddColumn("UPD_IP"   );
	ds_Copy_b.AddColumn("CRT_EMPID");
	ds_Copy_b.AddColumn("CRT_IP"   );	
		
	if(result  == "0"){	
		 
		ds_Copy_a.UpdateControl = false;
		ds_Copy_b.UpdateControl = false;
	
		for(var i=0; i<ds_Copy_a.GetRowCount(); i++){
			
			ds_Copy_a.SetRowType(i, "insert");
			ds_Copy_b   .SetRowType(i, "insert");
			
			ds_Copy_a.SetColumn(i, "BASE_YEAR" , cob_BaseYear.Value  );
			ds_Copy_a.SetColumn(i, "BRANCH_CD" , cob_ObjectFlag.Value);
			ds_Copy_a.SetColumn(i, "ZIP_NO1"   , cob_CountyFlag.Value);			 			
			ds_Copy_a.SetColumn(i, "UPD_EMPID" , USER_ID );
			ds_Copy_a.SetColumn(i, "UPD_IP"    , USER_IP );	
			ds_Copy_a.SetColumn(i, "CRT_EMPID" , USER_ID );
			ds_Copy_a.SetColumn(i, "CRT_IP"    , USER_IP );
			
			ds_Copy_b.SetColumn(i, "BASE_YEAR" , cob_BaseYear.Value  );
			ds_Copy_b.SetColumn(i, "BRANCH_CD" , cob_ObjectFlag.Value);
			ds_Copy_b.SetColumn(i, "ZIP_NO1"   , cob_CountyFlag.Value);			 			
			ds_Copy_b.SetColumn(i, "UPD_EMPID" , USER_ID );
			ds_Copy_b.SetColumn(i, "UPD_IP"    , USER_IP );	
			ds_Copy_b.SetColumn(i, "CRT_EMPID" , USER_ID );
			ds_Copy_b.SetColumn(i, "CRT_IP"    , USER_IP );
		}
		
	}else{	
		for(var i=0; i<ds_Copy_a.GetRowCount(); i++){								
			
			ds_Copy_a.SetColumn(i, "UPD_EMPID" , USER_ID );
			ds_Copy_a.SetColumn(i, "UPD_IP"    , USER_IP );
			
			ds_Copy_b.SetColumn(i, "UPD_EMPID" , USER_ID );
			ds_Copy_b.SetColumn(i, "UPD_IP"    , USER_IP );
		}
	}	
	
	var strSvcID         = "";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1_RoadLeaseObjRegisMgr-service" ;
	var strInDatasets    = "ds_Copy_a=ds_Copy_a:u ds_Copy_b=ds_Copy_b:u";
	var strOutDatasets   = "";		
	var strArgument      = "insert_a";	
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			    );
	
	if(ErrorCode<0)
	{    
		return false;
	}
	
	//"저장을 완료하였습니다."
	alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true);
}

//-------------------------------------------------------------------------------
// 함수명: fn_CommonExcelExport()
// 설명: 엑셀 button 실행, 조회된데이터를 엑셀형식으로변환한다
// arguments	없음
// return  없음
//-------------------------------------------------------------------------------
function fn_CommonExcelExport()
{	
	ds_Dummy.Copy(ds_Copy_a);
	ds_Dummy.AppendDataset(ds_Copy_b);
	ds_Dummy.AppendDataset(ds_Copy_c);
	
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "기준년도 : " + RPad(cob_BaseYear.Text," ",20);        
    SearchCondition = SearchCondition + "지사 : "     + RPad(cob_ObjectFlag.Text," ",20);
    SearchCondition = SearchCondition + "시/구 : "    + RPad(cob_CountyFlag.Text," ",20);
    gfn_ExportExcel("도로상점용대상등록", 5, SearchCondition, "grd_Excel");             
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Count()
//   설명  :  도로점용 시설물 설치현황 등록여부 조회
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Count()
{	
    var strSvcID        = "";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_RoadLeaseObjRegisMgr-service"
						+ "&BASE_YEAR="      + cob_BaseYear.Value
						+ "&BRANCH_CD="      + cob_ObjectFlag.Value
						+ "&ZIP_NO1="	     + cob_CountyFlag.Value
						;
	
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Count=ds_Count";
	var strArgument     = "count";
	 	 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	return ds_Count.GetColumn(0, "NCOUNT");
}

function cob_BaseYear_OnMouseOver(obj,nX,nY)
{
	SaveType = '';
	
	Grid0.SetCellProp("head",1  ,"Text",parseInt(cob_BaseYear.Value)-1 + '년말 GIS현황');
	Grid0.SetCellProp("head",2  ,"Text",parseInt(cob_BaseYear.Value)-1 + '년 납부금액' );
	Grid0.SetCellProp("head",3  ,"Text",parseInt(cob_BaseYear.Value)-1 + '년 동일배관' );
	Grid0.SetCellProp("head",11 ,"Text",cob_BaseYear.Value 			   + '년말 GIS현황');
	Grid0.SetCellProp("head",12 ,"Text",cob_BaseYear.Value			   + '년 납부금액' );
	
	Grid1.SetCellProp("head",1  ,"Text",parseInt(cob_BaseYear.Value)-1 + '년말 GIS현황');
	Grid1.SetCellProp("head",2  ,"Text",parseInt(cob_BaseYear.Value)-1 + '년 납부금액' );
	Grid1.SetCellProp("head",3  ,"Text",parseInt(cob_BaseYear.Value)-1 + '년 동일배관' );
	Grid1.SetCellProp("head",11 ,"Text",cob_BaseYear.Value 			   + '년말 GIS현황');
	Grid1.SetCellProp("head",12 ,"Text",cob_BaseYear.Value			   + '년 납부금액' );
	
	Grid2.SetCellProp("head",1  ,"Text",parseInt(cob_BaseYear.Value)-1 + '년말 GIS현황');
	Grid2.SetCellProp("head",2  ,"Text",parseInt(cob_BaseYear.Value)-1 + '년 납부금액' );
	Grid2.SetCellProp("head",3  ,"Text",parseInt(cob_BaseYear.Value)-1 + '년 동일배관' );
	Grid2.SetCellProp("head",11 ,"Text",cob_BaseYear.Value 			   + '년말 GIS현황');
	Grid2.SetCellProp("head",12 ,"Text",cob_BaseYear.Value			   + '년 납부금액' );
}

function ds_Copy_a_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{	

	if(strColumnID == "BEFORE_DIFF_LENG" || strColumnID == "CURRENT_DIFF_LENG"){
		ds_Copy_a.SetColumn(nRow, "DIFF_LENG", toNumber(ds_Copy_a.GetColumn(nRow, "CURRENT_DIFF_LENG"  ))
										     - toNumber(ds_Copy_a.GetColumn(nRow, "BEFORE_DIFF_LENG"   ))
							);
	}
	
	if(strColumnID == "CURRENT_LENG" || strColumnID == "BEFORE_LENG"){
		ds_Copy_a.SetColumn(nRow, "TOTAL_LENG", toNumber(ds_Copy_a.GetColumn(nRow, "CURRENT_LENG"  ))
										      - toNumber(ds_Copy_a.GetColumn(nRow, "BEFORE_LENG"   ))
		);
	}
	
	if(strColumnID == "TOTAL_LENG" || strColumnID == "DELETE_LENG" || strColumnID == "DIFF_LENG" || strColumnID == "NEW_LENG"){
	
		var str_TOTAL_LENG   = ds_Copy_a.GetColumn(nRow, "TOTAL_LENG"  );
		var str_DELETE_LENG  = ds_Copy_a.GetColumn(nRow, "DELETE_LENG" );
		var str_DIFF_LENG    = ds_Copy_a.GetColumn(nRow, "DIFF_LENG"   );
		var str_NEW_LENG	 = ds_Copy_a.GetColumn(nRow, "NEW_LENG"    );
		var str_REPORT_LENG  = 0;
		
		str_REPORT_LENG = toNumber(str_TOTAL_LENG) - (toNumber(str_DELETE_LENG) + toNumber(str_DIFF_LENG) + toNumber(str_NEW_LENG));
	
		ds_Copy_a.SetColumn(nRow, "REPORT_LENG", str_REPORT_LENG);
	}
	
	ds_Copy_c.SetColumn(nRow, strColumnID, toNumber(ds_Copy_a.GetColumn(nRow, strColumnID)) + toNumber(ds_Copy_b.GetColumn(nRow, strColumnID)));
}

function ds_Copy_b_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	
	if(strColumnID == "BEFORE_DIFF_LENG" || strColumnID == "CURRENT_DIFF_LENG"){
		ds_Copy_b.SetColumn(nRow, "DIFF_LENG", toNumber(ds_Copy_b.GetColumn(nRow, "CURRENT_DIFF_LENG"  ))
										     - toNumber(ds_Copy_b.GetColumn(nRow, "BEFORE_DIFF_LENG"   ))
							);
	}
	
	if(strColumnID == "CURRENT_LENG" || strColumnID == "BEFORE_LENG"){
		 ds_Copy_b.SetColumn(nRow, "TOTAL_LENG", toNumber(ds_Copy_b.GetColumn(nRow, "CURRENT_LENG"  ))
										       - toNumber(ds_Copy_b.GetColumn(nRow, "BEFORE_LENG"   ))
		 );
	}
	
	if(strColumnID == "TOTAL_LENG" || strColumnID == "DELETE_LENG" || strColumnID == "DIFF_LENG" || strColumnID == "NEW_LENG"){
	
		var str_TOTAL_LENG   = ds_Copy_b.GetColumn(nRow, "TOTAL_LENG"  );
		var str_DELETE_LENG  = ds_Copy_b.GetColumn(nRow, "DELETE_LENG" );
		var str_DIFF_LENG    = ds_Copy_b.GetColumn(nRow, "DIFF_LENG"   );
		var str_NEW_LENG	 = ds_Copy_b.GetColumn(nRow, "NEW_LENG"    );
		var str_REPORT_LENG  = 0;
		
		str_REPORT_LENG = toNumber(str_TOTAL_LENG) - (toNumber(str_DELETE_LENG) + toNumber(str_DIFF_LENG) + toNumber(str_NEW_LENG));
		
		ds_Copy_b.SetColumn(nRow, "REPORT_LENG", str_REPORT_LENG);
	}
	
	ds_Copy_c.SetColumn(nRow, strColumnID, toNumber(ds_Copy_a.GetColumn(nRow, strColumnID)) + toNumber(ds_Copy_b.GetColumn(nRow, strColumnID)));
}

