#include "JS::COMMONA10001.js";
#include "JS::COMMONA10002.js";

var saveFlag = ''; // INSERT, UPDATE FLAG

//---------------------------------------------------------
//	화면 OnLoadCompleted 이벤트처리 발생하는 함수( 화면ID_OnLoadCompleted )
//---------------------------------------------------------
function D1802020010U_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	fn_Init();
	fn_D1_Find(); // 초기 조회
}

//---------------------------------------------------------
//	초기 설정
//---------------------------------------------------------
function fn_Init()
{
	// 임시
	// 버튼을 강제 세팅할 경우만 사용
	// 권한관리 하면 사용말것 *** 순서대로 세팅
	//gfn_SetButton("0011011");
	
	//-----------------------------------------
	//  콤보박스의 초기 설정 : 기준년도
	//-----------------------------------------
	cob_BaseYear.Value      = toString(parseInt(Substr(Today(),0,4))-1);
	Grid0.SetCellProp("head",0,"Text",cob_BaseYear.Value + '년 단가');
}


/*===============================================================================
 * 공통 div_ComHead 에서 버튼 이벤트
		추가	fn_CommonAddRow
		삭제	fn_CommonDeleteRow
		저장	fn_CommonSave
		조회	fn_CommonSearch
		엑셀	fn_CommonExcelExport
		인쇄	fn_CommonPrint
		닫기	공통에서 처리
===============================================================================*/ 

//---------------------------------------------------------
//	공통 버튼 클릭 - 저장
//---------------------------------------------------------
function fn_CommonSave(){

	for(var i=0; i<ds_Pri.GetRowCount(); i++){
		if(gfn_IsNull(ds_Pri.GetColumn(i, "FIRST_SEOUL_UNIT_PRC"))){
			alert("갑지-서울(원)은 필수 입력값입니다. ");
			return;
		}
		if(gfn_IsNull(ds_Pri.GetColumn(i, "THIRD_GYEONGGI_UNIT_PRC"))){
			alert("병지-경기(원)은 필수 입력값입니다. ");
			return;
		}
	}
	
	fn_D1_Save();
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_SAVE
//   설명  : 도로 점용 단가를 등록/업데이트한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 

function fn_D1_Save()
{		
	var USER_ID    = gfn_GetGlobalValue("G_USER_NO");   // SESSION ID
	var USER_IP    = gfn_GetGlobalValue("G_IPADDRESS"); // SESSION IP
	
	ds_Pri.AddColumn("UPD_EMPID"           , "STRING", 256);	// ID
	ds_Pri.AddColumn("UPD_IP"              , "STRING", 256);	// IP
	ds_Pri.AddColumn("CRT_EMPID"           , "STRING", 256);	// ID
	ds_Pri.AddColumn("CRT_IP"              , "STRING", 256);	// IP
	ds_Pri.AddColumn("BASE_YEAR"           , "STRING", 256);	// IP
	ds_Pri.AddColumn("LEASE_FACI_DIAM_FLAG", "STRING", 256);	// IP
	

	ds_Pri.UpdateControl = false;
		
	var rCount = ds_Pri.GetRowCount();
	
	for(var i=0; i<rCount; i++) {		
		ds_Pri.SetRowType(i, saveFlag);
		ds_Pri.SetColumn (i, "UPD_EMPID", USER_ID       );
		ds_Pri.SetColumn (i, "UPD_IP"   , USER_IP      );	
		ds_Pri.SetColumn (i, "CRT_EMPID", USER_ID       );
		ds_Pri.SetColumn (i, "CRT_IP"   , USER_IP       );
		
		ds_Pri.SetColumn(i, "BASE_YEAR" , cob_BaseYear.Value);
	}
				
	var strSvcID         = "";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1_RoadLeaseUnitPrcRegisMgr-service"
						 + "&BASE_YEAR=" + cob_BaseYear.Value;
						 
	var strInDatasets    = "ds_Pri=ds_Pri:u";		
	var strOutDatasets   = "";		
	var strArgument      = "save";

	gfn_SyncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   	
	if(ErrorCode<0)
	{
		return false;
	}
	
	alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
	fn_D1_Find();
}

//---------------------------------------------------------
//	공통 버튼 클릭 - 조회
//---------------------------------------------------------
function fn_CommonSearch()
{
	fn_D1_Find();
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Find()
//   설명  :  도로 점용단가를 조회한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Find()
{		
	Grid0.SetCellProp("head",0,"Text",cob_BaseYear.Value + '년 단가');
	
    var strSvcID        = "";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_RoadLeaseUnitPrcRegisMgr-service&BASE_YEAR=" + cob_BaseYear.Value ;
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Pri=ds_Pri";
	var strArgument     = "find";
	 	 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ds_Pri.GetRowCount()==0) {
			Grid0.NoDataText = gfn_GetMessage("M000009","%AAA%","조회를");
			ds_Pri.Copy(ds_Menu);
			saveFlag = 'insert';
	}else{
		ds_Pri.SetColumn( 0, "col1", "0.1M 이하");
		ds_Pri.SetColumn( 1, "col1", "0.2M 이하");
		ds_Pri.SetColumn( 2, "col1", "0.4M 이하");
		ds_Pri.SetColumn( 3, "col1", "0.6M 이하");
		ds_Pri.SetColumn( 4, "col1", "0.8M 이하");
		ds_Pri.SetColumn( 5, "col1", "1.0M 이하");
		ds_Pri.SetColumn( 6, "col1", "2.0M 이하");
		ds_Pri.SetColumn( 7, "col1", "3.0M 이하");
		ds_Pri.SetColumn( 8, "col1", "3.0M 초과");
		ds_Pri.SetColumn( 9, "col1", "정배류기");
		ds_Pri.SetColumn(10, "col1", "RTU"     );
		saveFlag = 'update';
	}
}

//-------------------------------------------------------------------------------
// 함수명: fn_CommonExcelExport()
// 설명: 엑셀 button 실행, 조회된데이터를 엑셀형식으로변환한다
// arguments	없음
// return  없음
//-------------------------------------------------------------------------------
function fn_CommonExcelExport()
{
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "기준년도 : " +     RPad(cob_BaseYear.Text," ",20)  ;
        
    gfn_ExportExcel("도로점용단가 등록", 5, SearchCondition, "Grid0");
}

//-------------------------------------------------------------------------------
// 함수명: fn_CommonPrint()
// 설명: 출력 button 실행, 데이터를 출력 한다
// arguments	없음
// return  없음
//-------------------------------------------------------------------------------
function fn_CommonPrint(){
	PrintScreen();
}

function cob_BaseYear_OnMouseOut(obj)
{
	cob_BaseYear.Value      = Substr(Today(),0,4)+"년";
	Grid0.SetCellProp("head",0,"Text",cob_BaseYear.Value + '년 단가');
	fn_D1_Find();
}
