function D1802060010F_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	fn_Init();
	cob_BranchCd_OnChanged();
}


//---------------------------------------------------------
//	초기 설정
//---------------------------------------------------------
function fn_Init()
{
	gfn_D1_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_ObjectFlag"));	
	//-----------------------------------------
	//  콤보박스의 초기 설정 : 기준년도, 대상구분, 점용시설 순서
	//----------------------------------------
	cob_ObjectFlag.Index = 0;
}

/********************************************************************************
* 기      능   :  지사별 구를 조회하여 콤보박스에 셋팅
********************************************************************************/
function cob_BranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {

    // 지사별 구 콤보 셋팅 시
    fn_SetComboBox_Comcode("1", cob_ObjectFlag.Value, fn_setDsObject_Comcode("1", "cob_StrZipNo1"));
    cob_StrZipNo1.Value = "";
}


/*===============================================================================
 * 공통 div_ComHead 에서 버튼 이벤트
		추가	fn_CommonAddRow
		삭제	fn_CommonDeleteRow
		저장	fn_CommonSave
		조회	fn_CommonSearch
		엑셀	fn_CommonExcelExport
		인쇄	fn_CommonPrint
		닫기	공통에서 처리
===============================================================================*/ 

//---------------------------------------------------------
//	공통 버튼 클릭 - 조회
//---------------------------------------------------------
function fn_CommonSearch()
{
	fn_D1_Find();
}

//---------------------------------------------------------
//	공통 버튼 클릭 - 저장
//---------------------------------------------------------
function fn_CommonSave(){}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Find1()
//   설명  : 등록된 재산세 산출내역이 없을 경우 조회 한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Find()
{	
	var strSvcID        = "";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_PropertyTaxCalcuSpecMgr-service" 
					    + "&BRANCH_NM=" + cob_ObjectFlag.Text
	                    + "&GU_NM="     + decode(cob_StrZipNo1.Text, '전체', '', cob_StrZipNo1.Text)
						;
						
	var strInDatasets   = "";
	var strOutDatasets  = "ds_ProTax1=ds_ProTax1";
	var strArgument     = "find1";

	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ds_ProTax1.GetRowCount()==0) Grid2.NoDataText  = gfn_GetMessage("M000009","%AAA%","조회를");
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Save()
//   설명  : 재산세 산출내역을 조회 한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function Button0_OnClick(obj)
{
	gfn_Dialog("D10","D1802010010U","",1005,560,true,-1,-1);
}

//-------------------------------------------------------------------------------
// 함수명: btn_SingleReport_OnClick()
// 설명: 공통 레포트 팝업 함수를 호출한다(단일 보고서)
// return 내용: 없음
//-------------------------------------------------------------------------------
function btn_SingleReport_OnClick1(obj)
{
	if(ds_ProTax1.GetColumn(0, "INSTALL_YESR") == '' || ds_ProTax1.GetColumn(0, "INSTALL_YESR") == null ){
		alert(gfn_GetMessage("M000022","%AAA%","데이터가"));
		return;
	}
	
	// 함수 호출용 파라미터를 설정한다
	var strTitle    = "재산세 산출내역" ;   		  // 출력물 제목
    var strPart     = "D10" ;         	    		  // 시스템 명 
	var strReport   = "D1802060010P" ;  	    	  // 출력물 ID 
	var strParameter= "BRANCH_NM=" + cob_ObjectFlag.Text
	                + ",GU_NM="    + decode(cob_StrZipNo1.Text, '전체', '', cob_StrZipNo1.Text)
	                ;
	
	//공통 레포트 팝업 함수 호출 
	gfn_ReportPopup(strTitle,strPart,strReport,strParameter);	
}
