var strTotalKopipeExplorLeng       = 0;
var strTotalKopipeUnExplorLeng     = 0;
var strTotalMpipeExplorLeng        = 0;
var strTotalMpipeUnExplorLeng      = 0;
var strTotalNewKopipeUnExplorLeng  = 0;
var strTotalNewMpipeUnExplorLeng   = 0;
var strTotalKopipeUnexplorLengInsc = 0;
var strTotalMpipeUnexplorLengInsc  = 0;

var strExplorCnt   = 0;
var strUnexplorCnt = 0;

var strRowType    = "";
var strSearchDate = "";

var str_BranchCd   = "";
var str_CountyFlag = "";
//------------------------------------------------------------------------------- 
// 함수명: D1901010010F_OnLoadCompleted()
//   설명: 화면 로드 후 조회 조건 기본 셋팅
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function D1901010010F_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
    gfn_D1_All_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_BranchCd"));
	gfn_SetComboBox_Comcode("A10085",gfn_setDsObject_Comcode("cob_Weather"));
	
	cob_BranchCd.Value = '';	
	cob_BranchCd_OnChanged();
}

function cob_BranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	// 지사별 구 콤보 셋팅 시
    fn_SetComboBox_Comcode("1", cob_BranchCd.value, fn_setDsObject_Comcode("1", "cob_CountyFlag"));
    cob_CountyFlag.Value = '';    
    
    
   	str_BranchCd   = "";
	str_CountyFlag = "";
}

//---------------------------------------------------------
//	공통 버튼 클릭 - 조회
//---------------------------------------------------------
function fn_CommonSearch()
{
	strRowType     = "";
	strSearchDate  = "";
	
	str_BranchCd   = "";
	str_CountyFlag = "";
	
	if(cob_BranchCd.Value == "0" || (cob_CountyFlag.Value == '' || cob_CountyFlag.Value == null))
	{
		fn_D1_FindPlanAllBranch();
	}else{
		fn_D1_FindPlanBranch();
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_FindPlanAllBranch()
//   설명:  배관탐사 전체 대상계획을 조회한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_FindPlanAllBranch()
{
	ds_PipeRst.ClearData();
	
	if(cal_ExplorYmd.Value == "" || cal_ExplorYmd.Value == null)
	{
		alert(gfn_GetMessage("M000011","%AAA%","탐사일자를"));
		return;
	}
	if(!fn_DateCheckToday(cal_ExplorYmd))
	{
		return;
	}
	
	strSearchDate  = cal_ExplorYmd.Value;
	str_BranchCd   = cob_BranchCd.Value;
	str_CountyFlag = cob_CountyFlag.Value;
	
	
	var strSvcID       = "fn_D1_Find";
	var strPart        = "D10";	
	var strURL         = "handle.do?ServiceName=D1_PipingSechResltRegisMgr-service" 
				       + "&BRANCH_CD="  + cob_BranchCd.Value
				       + "&ZIP_NO1="    + cob_CountyFlag.Value
				       + "&BASE_YEAR="  + substr(cal_ExplorYmd.Value,0,4)
				       + "&EXPLOR_YMD=" + cal_ExplorYmd.Value
				       ;
	var strInDatasets  = "";
	var strOutDatasets = "ds_PipePln=ds_PipePln ds_PipeSumRstToday=ds_PipeSumRstToday ds_PipeSumRst=ds_PipeSumRst ds_PipeRstCnt=ds_PipeRstCnt ds_PipeRstExcel=ds_PipeRstExcel";
	var strArgument    = "findAllPln";
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	edt_KopipeExplorLengPln.Enable   = false;
	edt_KopipeUnExplorLengPln.Enable = false;
	edt_MpipeExplorLengPln.Enable    = false;
	edt_MpipeUnExplorLengPln.Enable  = false;
	
	edt_KopipeSumLengPln.Value       = "" ;
	edt_MpipeSumLengPln.Value        = "" ;
	edt_ExplorSumLengPln.Value   	 = "" ;
	edt_UnExplorSumLengPln.Value 	 = "" ;
	edt_TotalLengPln.Value       	 = "" ;
	
	edt_KopipeExplorLengToday.Text       = toNumber(replace(ds_PipeSumRstToday.GetColumn(0,"KOPIPE_EXPLOR_LENG"		  ), ",", ""));
	edt_KopipeUnExplorLengToday.Text     = toNumber(replace(ds_PipeSumRstToday.GetColumn(0,"KOPIPE_UNEXPLOR_LENG"	  ), ",", ""));
	edt_MpipeExplorLengToday.Text        = toNumber(replace(ds_PipeSumRstToday.GetColumn(0,"MPIPE_EXPLOR_LENG"		  ), ",", ""));
	edt_MpipeUnExplorLengToday.Text      = toNumber(replace(ds_PipeSumRstToday.GetColumn(0,"MPIPE_UNEXPLOR_LENG"	  ), ",", ""));
	edt_NewKopipeUnExplorLengToday.text  = toNumber(replace(ds_PipeSumRstToday.GetColumn(0,"NEW_KOPIPE_UNEXPLOR_LENG" ), ",", ""));
	edt_NewMpipeUnExplorLengToday.text   = toNumber(replace(ds_PipeSumRstToday.GetColumn(0,"NEW_MPIPE_UNEXPLOR_LENG"  ), ",", ""));
	edt_KopipeUnexplorLengInscToday.text = toNumber(replace(ds_PipeSumRstToday.GetColumn(0,"KOPIPE_UNEXPLOR_LENG_INSC"), ",", ""));
	edt_MpipeUnexplorLengInscToday.text  = toNumber(replace(ds_PipeSumRstToday.GetColumn(0,"MPIPE_UNEXPLOR_LENG_INSC" ), ",", ""));
	
	edt_KopipeSumLengToday.text 	= toNumber(replace(edt_KopipeExplorLengToday.Text     , ",", "")) + toNumber(replace(edt_KopipeUnExplorLengToday.Text    , ",", "")) ;
	edt_MpipeSumLengToday.text      = toNumber(replace(edt_MpipeExplorLengToday.Text      , ",", "")) + toNumber(replace(edt_MpipeUnExplorLengToday.Text     , ",", "")) ;
	edt_KopipeInscSumLengToday.text = toNumber(replace(edt_NewKopipeUnExplorLengToday.Text, ",", "")) + toNumber(replace(edt_KopipeUnexplorLengInscToday.Text, ",", "")) ;
	edt_MpipeInscSumLengToday.text  = toNumber(replace(edt_NewMpipeUnExplorLengToday.Text , ",", "")) + toNumber(replace(edt_MpipeUnexplorLengInscToday.Text , ",", "")) ;
	
	edt_ExplorSumLengToday.text       = toNumber(replace(edt_KopipeExplorLengToday.Text       , ",", "")) + toNumber(replace(edt_MpipeExplorLengToday.Text      , ",", "")) ;
	edt_NewUnExplorSumLengToday.text  = toNumber(replace(edt_NewKopipeUnExplorLengToday.Text  , ",", "")) + toNumber(replace(edt_NewMpipeUnExplorLengToday.Text , ",", "")) ;
	edt_UnExplorSumLengToday.text     = toNumber(replace(edt_KopipeUnExplorLengToday.Text     , ",", "")) + toNumber(replace(edt_MpipeUnExplorLengToday.Text    , ",", "")) ;	
	edt_UnExplorInscSumLengToday.text = toNumber(replace(edt_KopipeUnexplorLengInscToday.Text , ",", "")) + toNumber(replace(edt_MpipeUnexplorLengInscToday.Text, ",", "")) ;	
	edt_TotalLengToday.text 	  	  = toNumber(replace(edt_KopipeSumLengToday.Text		  , ",", "")) + toNumber(replace(edt_MpipeSumLengToday.Text         , ",", "")) ;
	edt_TotalInscLengToday.text 	  = toNumber(replace(edt_KopipeInscSumLengToday.Text	  , ",", "")) + toNumber(replace(edt_MpipeInscSumLengToday.Text     , ",", "")) ;
	
	//대상계획 합계 및 소계 구하기
	fn_D1_PlnSum();
	
	strTotalKopipeExplorLeng 	   = toNumber(replace(ds_PipeSumRst.GetColumn(0,"KOPIPE_EXPLOR_LENG"	   ), ",", "")) - toNumber(replace(edt_KopipeExplorLengToday.Text      , ",", ""));
	strTotalKopipeUnExplorLeng 	   = toNumber(replace(ds_PipeSumRst.GetColumn(0,"KOPIPE_UNEXPLOR_LENG"	   ), ",", "")) - toNumber(replace(edt_KopipeUnExplorLengToday.Text    , ",", ""));
	strTotalMpipeExplorLeng 	   = toNumber(replace(ds_PipeSumRst.GetColumn(0,"MPIPE_EXPLOR_LENG"		   ), ",", "")) - toNumber(replace(edt_MpipeExplorLengToday.Text       , ",", ""));
	strTotalMpipeUnExplorLeng      = toNumber(replace(ds_PipeSumRst.GetColumn(0,"MPIPE_UNEXPLOR_LENG"      ), ",", "")) - toNumber(replace(edt_MpipeUnExplorLengToday.Text     , ",", ""));
	strTotalNewKopipeUnExplorLeng  = toNumber(replace(ds_PipeSumRst.GetColumn(0,"NEW_KOPIPE_UNEXPLOR_LENG" ), ",", "")) - toNumber(replace(edt_NewKopipeUnExplorLengToday.Text , ",", ""));
	strTotalNewMpipeUnExplorLeng   = toNumber(replace(ds_PipeSumRst.GetColumn(0,"NEW_MPIPE_UNEXPLOR_LENG"  ), ",", "")) - toNumber(replace(edt_NewMpipeUnExplorLengToday.Text  , ",", ""));
	strTotalKopipeUnexplorLengInsc = toNumber(replace(ds_PipeSumRst.GetColumn(0,"KOPIPE_UNEXPLOR_LENG_INSC"), ",", "")) - toNumber(replace(edt_KopipeUnexplorLengInscToday.Text, ",", ""));
	strTotalMpipeUnexplorLengInsc  = toNumber(replace(ds_PipeSumRst.GetColumn(0,"MPIPE_UNEXPLOR_LENG_INSC" ), ",", "")) - toNumber(replace(edt_MpipeUnexplorLengInscToday.Text , ",", ""));	
	
	//탐사실적 소계 및 합계 구하기
	fn_D1_RstSum();
	//배관탐사이력 전체를 조회한다.
	fn_D1_FindAll();
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_FindPlanBranch()
//   설명:  배관탐사 대상계획을 조회한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_FindPlanBranch()
{	
	if(cal_ExplorYmd.Value == "" || cal_ExplorYmd.Value == null)
	{
		alert(gfn_GetMessage("M000011","%AAA%","탐사일자를"));
		return;
	}
	
	if(!fn_DateCheckToday(cal_ExplorYmd))
	{
		return;
	}
	strSearchDate  = cal_ExplorYmd.Value;
	str_BranchCd   = cob_BranchCd.Value;
	str_CountyFlag = cob_CountyFlag.Value;
	
	var strSvcID       = "fn_D1_Find";
	var strPart        = "D10";	
	var strURL         = "handle.do?ServiceName=D1_PipingSechResltRegisMgr-service"
					   + "&BRANCH_CD="  + cob_BranchCd.Value
					   + "&ZIP_NO1="    + cob_CountyFlag.Value
					   + "&BASE_YEAR="  + substr(cal_ExplorYmd.Value,0,4)
					   + "&EXPLOR_YMD=" + cal_ExplorYmd.Value
					   ;
	var strInDatasets  = "";
	var strOutDatasets = "ds_PipePln=ds_PipePln ds_PipeRstCnt=ds_PipeRstCnt ds_PipeSumRst=ds_PipeSumRst";
	var strArgument    = "findPln";
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );

	edt_KopipeSumLengPln.text   = "" ;
	edt_MpipeSumLengPln.text    = "" ;
	edt_ExplorSumLengPln.text   = "" ;
	edt_UnExplorSumLengPln.text = "" ;
	edt_TotalLengPln.text 	    = "" ;
	
	var str_BaseYear = ds_PipePln.GetColumn(0, "BASE_YEAR");
	
	// 조회된 건수가 없을 경우에 		
	if(str_BaseYear == '' || str_BaseYear == null)
	{		
		ds_PipePln.ClearData();
		ds_PipePln.AddRow();
		
		edt_KopipeExplorLengPln.Enable   = true;
		edt_KopipeUnExplorLengPln.Enable = true;
		edt_MpipeExplorLengPln.Enable    = true;
		edt_MpipeUnExplorLengPln.Enable  = true;
	}else
	{
		edt_KopipeExplorLengPln.Enable   = false;
		edt_KopipeUnExplorLengPln.Enable = false;
		edt_MpipeExplorLengPln.Enable    = false;
		edt_MpipeUnExplorLengPln.Enable  = false;
	}
		
	//대상계획 합계 및 소계 구하기
	fn_D1_PlnSum();

	//배관탐사이력 조회하기
	fn_D1_Find();
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_FindAll()
//   설명:  배관탐사이력 전체를 조회한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_FindAll()
{	
	strRowType = "ALL";
	
	if(ds_PipeRstCnt.RowCount() == "0")
	{
		alert("전 지사에 해당하는 모든 탐사 실적이 미등록 상태입니다. ");
		return;
	}
	
	var strMsg  = "";
		
	if(ds_PipeRstCnt.RowCount() > 0)
	{
		strMsg  =  "" ;
		
		for(var i=0;i<ds_PipeRstCnt.RowCount();i++)
		{
			strMsg += ds_PipeRstCnt.GetColumn(i,"BRANCH_NM") + " : " + ds_PipeRstCnt.GetColumn(i,"GU_NM") + "\n";
		}
		 		
		alert(strMsg + "탐사 실적이 미등록 상태입니다. ");
		
	}else
	{
		alert("전 지사에 해당하는 모든 탐사 실적이 등록되었습니다.");
		return;
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_Find()
//   설명:  배관탐사이력을 조회한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Find()
{	
	var strSvcID        = "fn_D1_Find";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_PipingSechResltRegisMgr-service"
						+ "&BRANCH_CD="  + cob_BranchCd.Value
						+ "&ZIP_NO1="    + cob_CountyFlag.Value
						+ "&EXPLOR_YMD=" + cal_ExplorYmd.Value
						;
						
	var strInDatasets   = "";
	var strOutDatasets  = "ds_PipeRst=ds_PipeRst ds_PipeRstNm=ds_PipeRstNm ds_PipeRstExcel=ds_PipeRstExcel ds_PipeCnt=ds_PipeCnt";
	var strArgument     = "findRst";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	if(ds_PipeRst.RowCount() == 0)
	{
		strRowType = "Add";
		ds_PipeRst.AddRow();
		
		str_BranchCd   = cob_BranchCd.Value;
    	str_CountyFlag = cob_CountyFlag.Value;
		
		//탐사장비 체크박스 초기화
		chk_ExplorEquipCd.value = 1;
		chk_ExplorEquipEtc.value = 0;
		chk_ExplorEquipCd_OnClick();
		
		//지형변경 체크박스 초기화
		ds_PipeRst.SetColumn(0,"CHG_LAND_BD","0");
		ds_PipeRst.SetColumn(0,"CHG_LAND_RD_EXP","0");
		ds_PipeRst.SetColumn(0,"CHG_LAND_RD_OPN","0");
		chk_ChgLandEtc.Value = 0;
		chk_ChgLandEtc_OnClick(chk_ChgLandEtc,"0");
		
		//최근 입력한 탐사자들 불러오기
		ds_PipeRst.SetColumn(0,"RESECH_NM1",ds_PipeRstNm.GetColumn(0,"RESECH_NM1"));
		ds_PipeRst.SetColumn(0,"RESECH_NM2",ds_PipeRstNm.GetColumn(0,"RESECH_NM2"));
		ds_PipeRst.SetColumn(0,"RESECH_NM3",ds_PipeRstNm.GetColumn(0,"RESECH_NM3"));
		ds_PipeRst.SetColumn(0,"RESECH_NM4",ds_PipeRstNm.GetColumn(0,"RESECH_NM4"));
		ds_PipeRst.SetColumn(0,"RESECH_NM5",ds_PipeRstNm.GetColumn(0,"RESECH_NM5"));
		
		ds_PipeRst.UpdateControl = false;
		ds_PipeRst.SetRowType(0,"normal");
	}else
	{
		//탐사장비 체크박스 셋팅
		if(ds_PipeRst.GetColumn(0,"EXPLOR_EQUIP_CD") == 1)
		{
			chk_ExplorEquipCd.value = 1;
			chk_ExplorEquipCd_OnClick();
		}else if(ds_PipeRst.GetColumn(0,"EXPLOR_EQUIP_CD") == 2)
		{
			chk_ExplorEquipEtc.value = 1;
			chk_ExplorEquipEtc_OnClick();
		}
		
		//지형변경 체크박스 셋팅
		if(ds_PipeRst.GetColumn(0,"CHG_LAND_ETC") != "" && ds_PipeRst.GetColumn(0,"CHG_LAND_ETC") != null)
		{
			chk_ChgLandEtc.value = 1;
			chk_ChgLandEtc_OnClick(chk_ChgLandEtc,"1");
		}	
	}
	//금일실적 초기화
	fn_D1_TodaySumInt();
}

//------------------------------------------------------------------------------- 
// 함수명: fn_CommonSave()
//   설명: 공통 저장 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonSave()
{	
	if(str_BranchCd == '' || str_BranchCd == null){
		alert("지사가 전체로 선택되어 있을 시 저장이 불가능 합니다.\n지사를 선택 후 다시 조회하십시오. ");
		return;
	}

	if(str_CountyFlag == '' || str_CountyFlag == null){
		alert("시/구가 전체로 선택되어 있을 시 저장이 불가능 합니다.\n시/구를 선택 후 다시 조회하십시오. ");
		return;
	}
	
	fn_D1_Save();
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_Save()
//   설명:  배관탐사실적등록 조회한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Save()
{
	//수정 등록시 셋팅할 사용자 정보
	if(str_BranchCd != cob_BranchCd.Value)
	{
		alert("지사를 변경 시 저장이 불가능합니다.\n지사를 변경시 다시 조회하십시오. ");
		return;
		
	}else if(str_CountyFlag != cob_CountyFlag.Value)
	{
		alert("시/구를 변경 시 저장이 불가능합니다.\n시/구를 변경시 다시 조회하십시오. ");
		return;
		
	}else if(strSearchDate != cal_ExplorYmd.Value)
	{
		alert('탐사일자를 변경 시 저장이 불가능합니다.\n탐사일자 변경시 다시 조회하십시오');
		return;
		
	}else if(ds_PipePln.GetRowType(0) == "insert")
	{		
		if(edt_KopipeExplorLengPln.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","탐사연장(본관) 대상계획을"));
			return;
		}
		
		if(edt_MpipeExplorLengPln.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","탐사연장(공급관) 대상계획을"));
			return;
		}
		
		if(edt_KopipeUnExplorLengPln.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","탐사불가연장(본관) 대상계획을"));
			return;
		}
		
		if(edt_MpipeUnExplorLengPln.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","탐사불가연장(공급관) 대상계획을"));
			return;
		}		
		
		ds_PipePln.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_PipePln.SetColumn(0, "UPD_IP"   , gfn_GetGlobalValue("G_IPADDRESS"));
		ds_PipePln.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_PipePln.SetColumn(0, "CRT_IP"   , gfn_GetGlobalValue("G_IPADDRESS"));
		ds_PipePln.SetColumn(0, "BASE_YEAR", SubStr(cal_ExplorYmd.Value,0,4));
		ds_PipePln.SetColumn(0, "ZIP_NO1"  , cob_CountyFlag.Value);
		ds_PipePln.SetColumn(0, "BRANCH_CD", cob_BranchCd.Value  );
		
	}else if(ds_PipePln.GetRowType(0) == "update")
	{
		ds_PipePln.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"  ));
		ds_PipePln.SetColumn(0, "UPD_IP"   , gfn_GetGlobalValue("G_IPADDRESS"));
		
		
		
	}else if(strRowType != "ALL")
	{		
		/*		
		if(edt_KopipeExplorLeng.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","탐사연장(본관)을"));
			return;
		}
		if(edt_MpipeExplorLeng.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","탐사연장(공급관)을"));
