			return;
		}
		if(edt_NewMpipeUnexplorLeng.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","신불탐(공급관)을"));
			return;
		}

		if(edt_KopipeUnexplorLeng.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","탐사불가(본관)을"));
			return;
		}
		if(edt_MpipeUnexplorLeng.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","탐사불가(공급관)을"));
			return;
		}
		if(edt_KopipeUnexplorLengInsc.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","탐사불탐(본관)을"));
			return;
		}
		if(edt_MpipeUnexplorLengInsc.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","탐사불탐(공급관)을"));
			return;
		}
		if(edt_ResechBlock.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","조사구간을"));
			return;
		}
		if(chk_ExplorEquipEtc.Value == 1 && edt_EquipEtc.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","탐사장비 기타를"));
			return;
		}
		if(cob_Weather.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","날씨를"));
			return;
		}
		if(chk_ChgLandEtc.Value == 1 && edt_LandEtc.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","지형변경 기타를"));
			return;
		}
		if(edt_UnexplorNoPermitWhy.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","탐사불가사유를"));
			return;
