		if(edt_ExplorRmk.Text == "")
		{
			alert(gfn_GetMessage("M000011","%AAA%","특기사항을"));
			return;
		}
	*/	
		if(ds_PipeRst.GetRowType(0) != "update"){
			ds_PipeRst.SetRowType(0,"insert");
		}
		
		ds_PipeRst.SetColumn(0, "UPD_EMPID" , gfn_GetGlobalValue("G_USER_NO"));
		ds_PipeRst.SetColumn(0, "UPD_IP"    , gfn_GetGlobalValue("G_IPADDRESS"));
