//금일실적 소계 및 합계 초기화.
function fn_D1_TodaySumInt()
{		
	edt_KopipeExplorLengToday.Text 	     = toNumber(replace(edt_KopipeExplorLeng.Text      ,",",""));
	edt_KopipeUnExplorLengToday.Text 	 = toNumber(replace(edt_KopipeUnexplorLeng.Text    ,",",""));
	edt_MpipeExplorLengToday.Text 	 	 = toNumber(replace(edt_MpipeExplorLeng.Text       ,",",""));
	edt_MpipeUnExplorLengToday.Text 	 = toNumber(replace(edt_MpipeUnexplorLeng.Text     ,",",""));
	edt_NewKopipeUnExplorLengToday.text  = toNumber(replace(edt_NewKopipeUnexplorLeng.Text ,",",""));
	edt_NewMpipeUnExplorLengToday.text   = toNumber(replace(edt_NewMpipeUnexplorLeng.Text  ,",",""));
	edt_KopipeUnexplorLengInscToday.text = toNumber(replace(edt_KopipeUnexplorLengInsc.Text,",",""));
	edt_MpipeUnexplorLengInscToday.text  = toNumber(replace(edt_MpipeUnexplorLengInsc.Text ,",",""));

	if(ds_PipeRstCnt.GetColumn(0,"CNT") == '0'){
		edt_KopipeExplorLengToday.Value       = 0;
		edt_KopipeUnExplorLengToday.Value     = 0;
		edt_MpipeExplorLengToday.Value        = 0;
		edt_MpipeUnExplorLengToday.Value      = 0;
		edt_NewKopipeUnExplorLengToday.Value  = 0;
		edt_NewMpipeUnExplorLengToday.Value   = 0;
		edt_KopipeUnexplorLengInscToday.Value = 0;
		edt_MpipeUnexplorLengInscToday.Value  = 0;
	}

	edt_KopipeSumLengToday.text     = toNumber(replace(edt_KopipeExplorLengToday.Text     , ",", "")) + toNumber(replace(edt_KopipeUnExplorLengToday.Text    , ",", "")) ;
	edt_MpipeSumLengToday.text      = toNumber(replace(edt_MpipeExplorLengToday.Text      , ",", "")) + toNumber(replace(edt_MpipeUnExplorLengToday.Text     , ",", "")) ;
	edt_KopipeInscSumLengToday.text = toNumber(replace(edt_NewKopipeUnExplorLengToday.Text, ",", "")) + toNumber(replace(edt_KopipeUnexplorLengInscToday.Text, ",", "")) ;
	edt_MpipeInscSumLengToday.text  = toNumber(replace(edt_NewMpipeUnExplorLengToday.Text , ",", "")) + toNumber(replace(edt_MpipeUnexplorLengInscToday.Text , ",", "")) ;
	
	edt_ExplorSumLengToday.text       = toNumber(replace(edt_KopipeExplorLengToday.Text      , ",", "")) + toNumber(replace(edt_MpipeExplorLengToday.Text      , ",", "")) ;
	edt_NewUnExplorSumLengToday.text  = toNumber(replace(edt_NewKopipeUnExplorLengToday.Text , ",", "")) + toNumber(replace(edt_NewMpipeUnExplorLengToday.Text , ",", "")) ;
	edt_UnExplorSumLengToday.text     = toNumber(replace(edt_KopipeUnExplorLengToday.Text    , ",", "")) + toNumber(replace(edt_MpipeUnExplorLengToday.Text    , ",", "")) ;	
	edt_UnExplorInscSumLengToday.text = toNumber(replace(edt_KopipeUnexplorLengInscToday.Text, ",", "")) + toNumber(replace(edt_MpipeUnexplorLengInscToday.Text, ",", "")) ;	
	edt_TotalLengToday.text           = toNumber(replace(edt_KopipeSumLengToday.Text         , ",", "")) + toNumber(replace(edt_MpipeSumLengToday.Text         , ",", "")) ;
	edt_TotalInscLengToday.text       = toNumber(replace(edt_KopipeInscSumLengToday.Text     , ",", "")) + toNumber(replace(edt_MpipeInscSumLengToday.Text     , ",", "")) ;
	
	strTotalKopipeExplorLeng       = toNumber(replace(ds_PipeSumRst.GetColumn(0,"KOPIPE_EXPLOR_LENG"	   ),",","")) - toNumber(replace(edt_KopipeExplorLengToday.Text      , ",", ""));
	strTotalKopipeUnExplorLeng     = toNumber(replace(ds_PipeSumRst.GetColumn(0,"KOPIPE_UNEXPLOR_LENG"	   ),",","")) - toNumber(replace(edt_KopipeUnExplorLengToday.Text    , ",", ""));
	strTotalMpipeExplorLeng        = toNumber(replace(ds_PipeSumRst.GetColumn(0,"MPIPE_EXPLOR_LENG"		   ),",","")) - toNumber(replace(edt_MpipeExplorLengToday.Text       , ",", ""));
	strTotalMpipeUnExplorLeng      = toNumber(replace(ds_PipeSumRst.GetColumn(0,"MPIPE_UNEXPLOR_LENG"	   ),",","")) - toNumber(replace(edt_MpipeUnExplorLengToday.Text     , ",", ""));
	strTotalNewKopipeUnExplorLeng  = toNumber(replace(ds_PipeSumRst.GetColumn(0,"NEW_KOPIPE_UNEXPLOR_LENG" ),",","")) - toNumber(replace(edt_NewKopipeUnExplorLengToday.Text , ",", ""));
	strTotalNewMpipeUnExplorLeng   = toNumber(replace(ds_PipeSumRst.GetColumn(0,"NEW_MPIPE_UNEXPLOR_LENG"  ),",","")) - toNumber(replace(edt_NewMpipeUnExplorLengToday.Text  , ",", ""));
	strTotalKopipeUnexplorLengInsc = toNumber(replace(ds_PipeSumRst.GetColumn(0,"KOPIPE_UNEXPLOR_LENG_INSC"),",","")) - toNumber(replace(edt_KopipeUnexplorLengInscToday.Text, ",", ""));
	strTotalMpipeUnexplorLengInsc  = toNumber(replace(ds_PipeSumRst.GetColumn(0,"MPIPE_UNEXPLOR_LENG_INSC" ),",","")) - toNumber(replace(edt_MpipeUnexplorLengInscToday.Text , ",", ""));
	
	//탐사실적 소계 및 합계 구하기
	fn_D1_RstSum();
}

