//탐사실적 소계 및 합계 구하기
function fn_D1_RstSum(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{	
	edt_KopipeExplorLengRst.Text       = toNumber(strTotalKopipeExplorLeng		) + toNumber(replace(edt_KopipeExplorLengToday.Text		 ,",", ""));
	edt_KopipeUnExplorLengRst.Text     = toNumber(strTotalKopipeUnExplorLeng	) + toNumber(replace(edt_KopipeUnExplorLengToday.Text	 ,",", ""));
	edt_MpipeExplorLengRst.Text        = toNumber(strTotalMpipeExplorLeng		) + toNumber(replace(edt_MpipeExplorLengToday.Text		 ,",", ""));
	edt_MpipeUnExplorLengRst.Text      = toNumber(strTotalMpipeUnExplorLeng	 	) + toNumber(replace(edt_MpipeUnExplorLengToday.Text	 ,",", ""));
	edt_NewKopipeUnExplorLengRst.Text  = toNumber(strTotalNewKopipeUnExplorLeng ) + toNumber(replace(edt_NewKopipeUnExplorLengToday.Text ,",", ""));
	edt_NewMpipeUnExplorLengRst.Text   = toNumber(strTotalNewMpipeUnExplorLeng  ) + toNumber(replace(edt_NewMpipeUnExplorLengToday.Text	 ,",", ""));
	edt_KopipeUnexplorLengInscRst.Text = toNumber(strTotalKopipeUnexplorLengInsc) + toNumber(replace(edt_KopipeUnexplorLengInscToday.Text,",", ""));
	edt_MpipeUnexplorLengInscRst.Text  = toNumber(strTotalMpipeUnexplorLengInsc ) + toNumber(replace(edt_MpipeUnexplorLengInscToday.Text ,",", ""));

	edt_KopipeSumLengRst.text 	  = toNumber(replace(edt_KopipeExplorLengRst.Text	  , ",", "")) + toNumber(replace(edt_KopipeUnExplorLengRst.Text	   , ",", "")) ;
	edt_MpipeSumLengRst.text 	  = toNumber(replace(edt_MpipeExplorLengRst.Text	  , ",", "")) + toNumber(replace(edt_MpipeUnExplorLengRst.Text	   , ",", "")) ;
	edt_KopipeInscSumLengRst.text = toNumber(replace(edt_NewKopipeUnExplorLengRst.Text, ",", "")) + toNumber(replace(edt_KopipeUnexplorLengInscRst.Text, ",", "")) ;
	edt_MpipeInscSumLengRst.text  = toNumber(replace(edt_NewMpipeUnExplorLengRst.Text , ",", "")) + toNumber(replace(edt_MpipeUnexplorLengInscRst.Text , ",", "")) ;
	
	edt_ExplorSumLengRst.text 	    = toNumber(replace(edt_KopipeExplorLengRst.Text		 , ",", "")) + toNumber(replace(edt_MpipeExplorLengRst.Text		 , ",", "")) ;
	edt_NewUnExplorSumLengRst.text  = toNumber(replace(edt_NewKopipeUnExplorLengRst.Text , ",", "")) + toNumber(replace(edt_NewMpipeUnExplorLengRst.Text , ",", "")) ;
	edt_UnExplorSumLengRst.text     = toNumber(replace(edt_KopipeUnExplorLengRst.Text	 , ",", "")) + toNumber(replace(edt_MpipeUnExplorLengRst.Text	 , ",", "")) ;
	edt_UnExplorInscSumLengRst.text = toNumber(replace(edt_KopipeUnexplorLengInscRst.Text, ",", "")) + toNumber(replace(edt_MpipeUnexplorLengInscRst.Text, ",", "")) ;
	edt_TotalLengRst.text	        = toNumber(replace(edt_KopipeSumLengRst.Text		 , ",", "")) + toNumber(replace(edt_MpipeSumLengRst.Text		 , ",", "")) ;
	edt_TotalInscLengRst.text 		= toNumber(replace(edt_KopipeInscSumLengRst.Text	 , ",", "")) + toNumber(replace(edt_MpipeInscSumLengRst.Text	 , ",", "")) ;
	
	edt_ExplorSumLengRt.text   = Round(toNumber(replace(edt_ExplorSumLengRst.Text  , ",", "")) / toNumber(replace(edt_ExplorSumLengPln.Text  ,",","")) * 100,2);
	edt_UnExplorSumLengRt.text = Round(toNumber(replace(edt_UnExplorSumLengRst.Text, ",", "")) / toNumber(replace(edt_UnExplorSumLengPln.Text,",","")) * 100,2);
	edt_TotalLengRt.text       = Round(toNumber(replace(edt_TotalLengRst.Text      , ",", "")) / toNumber(replace(edt_TotalLengPln.Text      ,",","")) * 100,2);
}

