function Button0_OnClick(obj)
{	
	if(ds_PipeRst.GetRowCount() != 0)
	{
		if(ds_PipeRst.GetRowType(0) == "normal" && strRowType != "Add")
		{
			if(cob_BranchCd.Value != 0 && cal_ExplorYmd.Value != "")
			{
				// 함수 호출용 파라미터를 설정한다
				var strTitle ="배관탐사일지"; 
				var strPart ="D10";           
				var strReport="D1901010010P";
				var strParameter="BASE_YEAR=" + substr(cal_ExplorYmd.Value,0,4) +  ",EXPLOR_YMD=" + cal_ExplorYmd.Value +",BRANCH_CD="+cob_BranchCd.Value + ",ZIP_NO1=" +cob_CountyFlag.Value;
				
				// 공통 레포트 팝업 함수 호출 
				gfn_ReportPopup(strTitle,strPart,strReport,strParameter);
			}
		}else{
			alert('저장 후 이용가능합니다.');
		}
	}else if(cob_BranchCd.Value == 0)
	{
		alert('지사는 전체를 선택할 수 없습니다.');
	}else{
		alert('조회 후 이용가능합니다.');
	}
}
function Button1_OnClick(obj)
{
	if(ds_PipeRst.GetRowCount() != 0)
	{
		if(strSearchDate != cal_ExplorYmd.Value)
		{
			alert('탐사일자를 변경 시 저장이 불가능합니다.\n탐사일자 변경시 다시 조회하십시오');
			return;
		}else if(ds_PipeRst.GetRowType(0) == "normal" && strRowType != "Add")
		{
			if(cob_BranchCd.Value != 0 && cal_ExplorYmd.Value != "")
			{
				var strParameter=substr(cal_ExplorYmd.Value,0,4)+","+cal_ExplorYmd.Value+","+cob_BranchCd.Value+","+cob_CountyFlag.Value;				
				
				gfv_callGWInfo("fmD22","D10","D1901010010P",4,strParameter);
			}
		}else{
			alert('저장 후 이용가능합니다.');
		}
	}else if(cob_BranchCd.Value == 0)
	{
		alert('지사는 전체를 선택할 수 없습니다.');
	}else{
		alert('조회 후 이용가능합니다.');
	}
}

