//-------------------------------------------------------------------------------
//   함수명: fn_CommonExcelExport()
//   설명  :  엑셀다운 
//   arguments   
//   obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 

function fn_CommonExcelExport()
{
	var SearchCondition= "";
	
    SearchCondition = SearchCondition + "지사 :"     + RPad(cob_BranchCd.Text   ," ",20);
    SearchCondition = SearchCondition + "시/구 :"    + RPad(cob_CountyFlag.Text ," ",20);
    SearchCondition = SearchCondition + "탐사일자 :" + RPad(cal_ExplorYmd.Text  ," ",20);
	gfn_ExportExcel("배관탐사실적등록 ", 5, SearchCondition, "Grid0"); 
}

