//배관탐사의뢰 소계 및 합계 구하기.
function fn_D1_ObjectCnt(obj,strPreText,strPostText)
{
	edt_TodaySum.text = toNumber(replace(edt_ExplorCnt.Text  ,",","")) 
					  + toNumber(replace(edt_UnexplorCnt.Text,",","")) ;
	
	if(edt_ExplorCnt == obj)
	{
		edt_ExplorTotalCnt.text = toNumber(replace(edt_ExplorTotalCnt.Text,",",""))
							    - toNumber(replace(strPreText             ,",","")) 
								+ toNumber(replace(strPostText            ,",","")) ;
	
	}else if(edt_UnexplorCnt == obj)
	{
		edt_UnexplorTotalCnt.text = toNumber(replace(edt_UnexplorTotalCnt.Text,",","")) 
								  - toNumber(replace(strPreText				  ,",","")) 
								  + toNumber(replace(strPostText			  ,",","")) ;
	}
		
		edt_TotalCnt.text = toNumber(replace(edt_ExplorTotalCnt.Text  ,",","")) 
						  + toNumber(replace(edt_UnexplorTotalCnt.Text,",","")) ;
}

