var NmKor = " ";		//의뢰자 승인자
var EMP_ID = "";	//의뢰자 승인자
var CurDate = "";	//현제날짜
var RightFlag = 0;	//0 :권한없음 , 1 :권한있음

var edt_ExplorReqEmpcd = "";	//의뢰자사번(검색)

var branchcd = "";
