}

function fn_d1_team(branchcd)
{
	var strSvcID = "";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DiggingWorkMgr-service&teamchk=1"
	           + "&BRANCH_CD="+branchcd
	           ;
	var strInDatasets  = "";
	var strOutDatasets  = "";
	    strOutDatasets += "ds_team=ds_team";
	var strArgument  = "";
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ErrorCode < 0)
	{
		return;
	}
	ds_team.AddRow();
    ds_team.SetColumn(ds_team.GetRowCount()-1,"BRANCH_CD","");
    ds_team.SetColumn(ds_team.GetRowCount()-1,"DEPRT_NM_KOR","전체");
    ds_team.SetColumn(ds_team.GetRowCount()-1,"DEPART_DPT_CD","");
}

function fn_D1_WorkNumPopUp(obj)
{
    var Arguments = "";
	var Result = gfn_Dialog("E20", "E2101010020U", Arguments,750,550);

	if(Result.length > 0)
	{	
		edt_ExplorReqEmpcd = Result[0];	//사원번호
		edt_ExplorReqEmpnm = Result[1];	//한글사원명		
	}	
}

function grd_HeadData_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 9){
		if(ds_HeadData.GetColumn(nRow,"EXPLOR_APPRONM_CK") == 1){
			alert("이미 승인된 건입니다.");
			return;
		}else{
			var Arguments = "";
			var Result = gfn_Dialog("E20", "E2101010020U", Arguments,750,550);
		
			if(Result.length > 0)
			{	
				ds_HeadData.SetColumn(nRow,"EXPLORER",Result[0]);	//사원번호
				ds_HeadData.SetColumn(nRow,"EXPLORERNM",Result[1]);	//한글사원명			
				ds_HeadData.SetColumn(nRow,"EXPLOR_YMD",CurDate);	//탐사일자					
			}
		}
	}
		
	if(nCell == 13){
	
		if(RightFlag == 0){
			alert("권한이 없습니다.");
			if(ds_HeadData.GetColumn(nRow,"EXPLOR_APPRONM_CK") == 1){		
				ds_HeadData.SetColumn(nRow,"EXPLOR_APPRONM_CK",0);					
			}else{
				ds_HeadData.SetColumn(nRow,"EXPLOR_APPRONM_CK",1);		
			}
			return false;
		}	
		//-----------------------------------------
		//  승인 클릭
		//-----------------------------------------		
		if(ds_HeadData.GetColumn(nRow,"EXPLOR_APPRONM_CK") == 1){		
			ds_HeadData.SetColumn(nRow,"EXPLOR_APPRO","");		
			ds_HeadData.SetColumn(nRow,"EXPLOR_APPRONM","");				
			ds_HeadData.SetColumn(nRow,"EXPLOR_APPRO_YMD","");						
		}else{
			ds_HeadData.SetColumn(nRow,"EXPLOR_APPRO",EMP_ID);		
			ds_HeadData.SetColumn(nRow,"EXPLOR_APPRONM",NmKor);				
			ds_HeadData.SetColumn(nRow,"EXPLOR_APPRO_YMD",CurDate);
		}
	}
}

