	strmpipe_id   = ds_Dcvg.GetColumn(nRow,"MPIPE_ID");
	fn_D1_find1(strbase_year,strmpipe_id);
}

function ds_DcvgArRst_OnRowPosChanged(obj,nOldRow,nRow)
