	strbase_year  = ds_DcvgArRst.GetColumn(nRow,"BASE_YEAR");
	strmpipe_id   = ds_DcvgArRst.GetColumn(nRow,"MPIPE_ID");
	fn_D1_find2(strbase_year, strmpipe_id);
}

function fn_D1_deleteRow1(obj)
{
	if(ds_DcvgRst.GetRowType(ds_DcvgRst.row) == "insert"){
		ds_DcvgRst.DeleteRow(ds_DcvgRst.row);
		return;
	}

	if(ds_DcvgRst.GetRowCount() == 0)
	{
		return;
	}

	if(ds_DcvgArRst.GetColumn(ds_DcvgArRst.row, "DECIDE_PROC_STS") == '0' || ds_DcvgArRst.GetColumn(ds_DcvgArRst.row, "DECIDE_PROC_STS") == '1'
		|| ds_DcvgArRst.GetColumn(ds_DcvgArRst.row, "DECIDE_PROC_STS") == '2' )
	{
		alert("결재 진행/완료건으로 삭제가 불가능합니다...");
		return;
	}
	
	if(confirm("DCVG 탐사 손상내역을 삭제하시겠습니까?"))
	{
		ds_DcvgRst.DeleteRow(ds_DcvgRst.row);
	
		iGridPos = ds_Dcvg.row;
	
		var strSvcID         = "";
		var strPart          = "D10";	
		var strURL           = "handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeMgr-service" ;	
		var strInDatasets    = "ds_DcvgRst=ds_DcvgRst:u";
		var strOutDatasets   = "";		
		var strArgument      = "save_Rst";
			 
		gfn_SyncCall( 
						 strSvcID
					   , strPart
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
				   );
		
		if(ErrorCode < 0)
		{
			return;
		}
	
		alert("정상 삭제 되었습니다.");
		
		fn_D1_find();
	}	
}

function btn_Com03_OnClick(obj)
{
	for(var i =0; i<ds_DcvgRst.GetRowCount();i++)
	{
		if(ds_DcvgRst.GetRowType(i)=="insert" || ds_DcvgRst.GetRowType(i)=="update")
		{
			if(ds_DcvgArRst.GetColumn(ds_DcvgArRst.row, "EXPLOR_YMD") == '' || ds_DcvgArRst.GetColumn(ds_DcvgArRst.row, "EXPLOR_YMD") == null)
			{
				alert("배관탐사일자를 지정하세요. ");
				return;
			}
			
			if(ds_DcvgArRst.GetColumn(ds_DcvgArRst.row, "DECIDE_PROC_STS") == '0' || ds_DcvgArRst.GetColumn(ds_DcvgArRst.row, "DECIDE_PROC_STS") == '1'
			    || ds_DcvgArRst.GetColumn(ds_DcvgArRst.row, "DECIDE_PROC_STS") == '2' )
			{
				alert("결재 진행/완료건으로 둥록 및 수정이 불가능합니다...");
				return;
			}				
			
			ds_DcvgRst.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//수정자사번
			ds_DcvgRst.SetColumn(i, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//수정자 IP
			ds_DcvgRst.SetColumn(i, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
			ds_DcvgRst.SetColumn(i, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP
		}	
		else
		{	
			ds_DcvgRst.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//수정자사번
			ds_DcvgRst.SetColumn(i, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//수정자 IP
		}
	}
	
	iGridPos = ds_Dcvg.row;
	
	var strSvcID         = "";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeMgr-service" ;	
	var strInDatasets    = "ds_DcvgRst=ds_DcvgRst:u";
	var strOutDatasets   = "";		
	var strArgument      = "save_Rst";
	
	gfn_syncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				);
			   
	if(ErrorCode<0)
	{
		return false;
	}
	
	alert("정상 저장/수정 되었습니다.");	
	
	fn_D1_find();
}

function Grid_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if("EXPLOR_YMD" == Grid.GetCellProp("body",nCell,"ColId"))
	{
		gfn_SetCalendar(obj, nRow, nCell);
	}
}

function fn_D1_decide()
{
	ds_Decide.ClearData();
	
	var strSvcID        = "decide_yn";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeMgr-service";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Decide=ds_Decide";
	var strArgument     = "decide_yn=1";
		strArgument		+= " BASE_YEAR="  + quote(substr(today(),0,4));
		strArgument		+= " EXPLOR_YMD=" + quote(ds_DcvgArRst.GetColumn(ds_DcvgArRst.row, "EXPLOR_YMD"));
		strArgument		+= " BRANCH_CD="  + quote(cob_ObjectFlag.Value);

	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}			   
