//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Find()
//   설명  :  계획실적 조회
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_find()
{	
	ds_Dcvg.ClearData();
	
    var strSvcID        = "view_Pln";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeMgr-service";
	var strInDatasets   = "";
	//var strOutDatasets  = "ds_Dcvg=ds_Dcvg ds_Excel=ds_Excel";
	var strOutDatasets  = "ds_Dcvg=ds_Dcvg";
	var strArgument     = "view_Pln=1";
	 	strArgument		+= " BASE_YEAR="  + quote(sp_Year.Value);
		strArgument		+= " BRANCH_CD="  + quote(cob_ObjectFlag.Value);
		strArgument		+= " ZIP_NO1="    + quote(cob_CountyFlag.Value);
		strArgument		+= " MPIPE_ID="	  + quote(trim(edt_MpipeSearch.Text));
		strArgument		+= " EXPLOR_FLAG="   + '2';
		strArgument		+= " EXPLOR_END_YN=" + '';

	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}

