						+ "&BASE_YEAR="  + strbase_year
						+ "&MPIPE_ID="   + strmpipe_id
						;
	var strInDatasets   = "";
	var strOutDatasets  = "ds_DcvgArRst=ds_DcvgArRst";
	var strArgument     = "view_ArRst";
	 	 
	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ds_DcvgArRst.GetRowCount()==0) {
		ds_DcvgRst.ClearData();
	}
}

