function fn_D1_Excel(){

	var SearchCondition= "";
    	
	//2012.12.20 이건민 - 권기철 과장님 추가요청사항 (탐사일자 대신 기준년도로 교체)
	/*
    SearchCondition = SearchCondition + "지사 : " +     RPad(cob_ObjectFlag.Text," ",20);
    SearchCondition = SearchCondition + "시/구 : " +    RPad(cob_CountyFlag.Text," ",20);
    SearchCondition = SearchCondition + "탐사일자 : " + RPad(substr(today(),0,4) + '0101' + ' ~ ' + today()," ",40);
	SearchCondition = SearchCondition + "배관번호 : " + RPad(edt_MpipeSearch.Text," ",20);
    */
    
    SearchCondition = SearchCondition + "지사 : " +     RPad(cob_ObjectFlag.Text," ",20);
    SearchCondition = SearchCondition + "시/구 : " +    RPad(cob_CountyFlag.Text," ",20);
    SearchCondition = SearchCondition + "기준년도 : " + RPad(sp_Year.Value," ",40);
	SearchCondition = SearchCondition + "배관번호 : " + RPad(edt_MpipeSearch.Text," ",20);
        
    gfn_ExportExcel("DCVG탐사실적등록_비계획배관내역", 5, SearchCondition, "Grid1");             
}

