//------------------------------------------------------------------------------- 
//   함수명 : fn_D1_SAVE
//   설명  : 손상위치에 따른 조치사항 내용을 저장한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
//-------------------------------------------------------------------------------
//  Transition   : Service id
//  ServicePart   : Service part
//  Url          : Server url
//  InputDsList   : Input Dataset List
//  OutputDsList   : Output Dataset List
//  Argument   :  Argument
//-------------------------------------------------------------------------------
//------------------------------------------------------------------------------- 
//   함수명 : fn_D1_SAVE1
//   설명  : 결과 실적에 등록된 내용을 저장한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_save(){	

	if(ds_DcvgRst.RowCount() == 0)
	{
		return;
	}	
	
	if(ds_DcvgDamage.GetColumn(ds_DcvgDamage.row, "ZIP_NO1") == '' || ds_DcvgDamage.GetColumn(ds_DcvgDamage.row, "ZIP_NO1") == null ){
		alert("사고위치 정보를 입력하세요. ");
		return;
	}
	
	// 2012.01.03 이건민 - 권기철 과장님 추가 요청사항 (압력 / 관경은 저장에서 제외)
	/*
	if(ds_DcvgDamage.GetColumn(ds_DcvgDamage.row, "PRESS_FLAG") == '' || ds_DcvgDamage.GetColumn(ds_DcvgDamage.row, "PRESS_FLAG") == null ){
		alert("압력을 선택하세요. ");
		return;
	}
	
	if(ds_DcvgDamage.GetColumn(ds_DcvgDamage.row, "PIPE_DIAM_CD") == '' || ds_DcvgDamage.GetColumn(ds_DcvgDamage.row, "PIPE_DIAM_CD") == null ){
		alert("관경을 선택하세요. ");
		return;
	}
	*/
	
	if(ds_DcvgDamage.GetColumn(ds_DcvgDamage.row, "DAMAGE_CAUSE") == '' || ds_DcvgDamage.GetColumn(ds_DcvgDamage.row, "DAMAGE_CAUSE") == null ){
		alert("손상원인을 선택하세요. ");
		return;
	}
	
	if(ds_DcvgDamage.GetColumn(ds_DcvgDamage.row, "DAMAGE_RMK") == '' || ds_DcvgDamage.GetColumn(ds_DcvgDamage.row, "DAMAGE_RMK") == null ){
		alert("조치사항을 입력하세요. ");
		return;
	}
	
	if(ds_DcvgDamage.GetRowType(ds_DcvgDamage.row) == 'insert')
	{
		ds_DcvgDamage.SetColumn(ds_DcvgDamage.row, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//수정자사번
		ds_DcvgDamage.SetColumn(ds_DcvgDamage.row, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//수정자 IP
		ds_DcvgDamage.SetColumn(ds_DcvgDamage.row, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
		ds_DcvgDamage.SetColumn(ds_DcvgDamage.row, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP	
	}
	else if(ds_DcvgDamage.GetRowType(ds_DcvgDamage.row) == 'update')	
	{
		ds_DcvgDamage.SetColumn(ds_DcvgDamage.row, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//수정자사번
		ds_DcvgDamage.SetColumn(ds_DcvgDamage.row, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//수정자 IP	
	}
	else
	{
		return;
	}	
	
	var strSvcID         = "saveDamage";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeMgr-service" ;	
	var strInDatasets    = "ds_DcvgDamage=ds_DcvgDamage:u";
	var strOutDatasets   = "";		
	var strArgument      = "saveDamage=1";
		
	gfn_syncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			    );
			   
	if(ErrorCode<0)
	{
		return false;
	}
	
	alert("정상적으로 저장 되었습니다...");
	
	fn_D1_find();
}

function fn_D1_deleteRow()
{
	if(ds_DcvgDamage.RowCount() == 0 || ds_DcvgDamage.GetRowType(ds_DcvgDamage.row) == 'insert')
	{
		ds_DcvgDamage.DeleteRow(ds_DcvgDamage.row);
		return;
	}
	
	if(confirm("DCVG 손상부 내역을 삭제하시겠습니까?"))
	{
		ds_DcvgDamage.DeleteRow(ds_DcvgDamage.row);
			
		var strSvcID         = "saveDamage";
		var strPart          = "D10";	
		var strURL           = "handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeMgr-service" ;	
		var strInDatasets    = "ds_DcvgDamage=ds_DcvgDamage:u";
		var strOutDatasets   = "";		
		var strArgument      = "saveDamage=1";
			
		gfn_syncCall( 
						 strSvcID
					   , strPart  	 
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
					);
				   
		if(ErrorCode<0)
		{
			return false;
		}
		
		alert("정상적으로 삭제 되었습니다...");
		
		fn_D1_find();
	}	
}
//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Find()
//   설명  :  조치결과 조회
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_find()
{	    
	ds_DcvgRst.ClearData();
	ds_DcvgDamage.ClearData();
	
	var strSvcID        = "search_rst";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeMgr-service";
	var strInDatasets   = "";
	//var strOutDatasets  = "ds_DcvgRst=ds_DcvgRst ds_Excel=ds_Excel1";
	var strOutDatasets  = "ds_DcvgRst=ds_DcvgRst";
	var strArgument     = "search_rst=1"; 
		strArgument		+= " BASE_YEAR="  + quote(sp_Year.Value);
		strArgument		+= " BRANCH_CD=" + quote(cob_ObjectFlag.Value);
		strArgument		+= " ZIP_NO1="  + quote(cob_CountyFlag.Value);
		strArgument		+= " MPIPE_ID="  + quote(trim(edt_MpipeSearch.Text));
		
		// 2013.01.02 이건민 - 권기철 과장님 추가요청사항 (탐사기간 삭제요청)
		//strArgument		+= " EXPLOR_YMD_FROM="  + quote(cal_Ymd_from.Value);
		//strArgument		+= " EXPLOR_YMD_TO="  + quote(cal_Ymd_to.Value);
		
	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}

// GIS  수정의뢰서
function Button91_OnClick(obj)
{
	if(ds_DcvgRst.GetColumn(ds_DcvgRst.row, "GIS_YN") == 'Y'){
		alert("이미 GIS 수정의뢰서가 작성된 건입니다. ");
		return;
	}	
	
	if(ds_DcvgDamage.GetColumn(0, "DAMAGE_YN")  != 'Y'){
		alert("손상유무가 확인된 경우에만 작성 가능합니다. ");
		return;
	}
	gfn_Dialog("D10","D1402060010U","",1024,1288,true,-1,-1);
}

//-------------------------------------------------------------------------------
// 함수명: fn_ZipSearch()
//   설명: 우편번호 조회클릭시 우편번호 조회 popup화면 로딩
// arguments   
//          obj: 사용된 객체를 전달 한다.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_ZipSearch(obj)
{
	//-----------------------------------------
	//  주소검색 팝업창을 부른다 
	//-----------------------------------------
	var arrList = gfn_Dialog("A10", "A1101020010U", "", 1100, 600, true, -1, -1);
	
	if(arrList == 0)
	{
		return;
	}		
		
		if (arrList != "" && arrList != null) 
		{	
			if (ds_DcvgRst.GetColumn(ds_DcvgRst.row, "ZIP_NO1") != arrList[2]) 
			{
				 alert("해당 시/구를 선택해주세요.");
				 return;
			}
		}	

	if(obj.id  == 'btn_DZip')
	{
		edt_Addr.Text     =   arrList[17];  	  // 지번주소조합
		edt_ZipNo1.Text   =   arrList[2];         // 우편번호1
		edt_ZipNo2.Text   =   arrList[3];         // 우편번호2
	}	
		
	G_ACTIVEWIN = parent.id;
}		

//-------------------------------------------------------------------------------
// 함수명: Button0_OnClick()
//   설명: 첨부파일 popup화면 로딩
// arguments   
//          obj: 사용된 객체를 전달 한다.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function Button0_OnClick(obj)
{	
	var arg = "fa_Url=handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeMgr-service"
			+ "&BASE_YEAR="            + ds_DcvgRst.GetColumn(ds_DcvgRst.row, "BASE_YEAR")
			+ "&MPIPE_ID="		       + ds_DcvgRst.GetColumn(ds_DcvgRst.row, "MPIPE_ID")
			+ "&EXPLOR_YMD="		   + ds_DcvgRst.GetColumn(ds_DcvgRst.row, "EXPLOR_YMD")
			+ "&EXPLOR_BLOCK_SEQ="     + ds_DcvgRst.GetColumn(ds_DcvgRst.row, "EXPLOR_BLOCK_SEQ")
			+ "&EXPLOR_RSLT_SEQ="      + ds_DcvgRst.GetColumn(ds_DcvgRst.row, "EXPLOR_RSLT_SEQ")
			+ " fa_Part=D10"
			;
			
	var arrList = gfn_Dialog("D10","D1101010018U",arg,450,280,true,-1,-1);
	
	edtFileList.text = "";
	
	for(var i=0; i<arrList.length; i++)
	{
		edtFileList.text += arrList[i];
		if(arrList.length-1 != i) edtFileList.text += "/";
	}
	
	if(edtFileList.text == null || edtFileList.text == '') return;
	
	fn_D1_AttachFileYN();
}

