//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_d1_FileSearch(nRow)
{	
	var strSvcID = "";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeMgr-service&findFile=1"
	           + "&BASE_YEAR="         + ds_DcvgRst.GetColumn(nRow, "BASE_YEAR")
			   + "&MPIPE_ID="		   + ds_DcvgRst.GetColumn(nRow, "MPIPE_ID")
			   + "&EXPLOR_YMD="		   + ds_DcvgRst.GetColumn(nRow, "EXPLOR_YMD")
			   + "&EXPLOR_BLOCK_SEQ="  + ds_DcvgRst.GetColumn(nRow, "EXPLOR_BLOCK_SEQ")
			   + "&EXPLOR_RSLT_SEQ="   + ds_DcvgRst.GetColumn(nRow, "EXPLOR_RSLT_SEQ")
	           ;
	           
	var strInDatasets   = "";
	var strOutDatasets  = "";
	    strOutDatasets += "ds_FileList=ds_FileList";
	var strArgument  = "";
		
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ErrorCode < 0)
	{
		alert(ErrorMsg);
		return;
	}
		
	edtFileList.text = "";	
		
	for(var i=0; i<ds_FileList.RowCount(); i++)
	{
		edtFileList.text += ds_FileList.GetColumn(i,"ORG_FILE_NAME");
			if(ds_FileList.RowCount()-1 != i) edtFileList.text += "/";
		}
	
}

