		//Edit27.Enable = false;
		//Edit27.Text   = ''   ;
	//	Static49.Color = null;
	//}
}
function ds_DcvgRst_OnRowPosChanged(obj,nOldRow,nRow)
{
	if(nRow < 0) return;
	
	ds_DcvgDamage.ClearData();
	
	var strSvcID        = "view_dcvgdamage";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeMgr-service";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_DcvgDamage=ds_DcvgDamage";
	var strArgument     = "view_dcvgdamage=1";
		strArgument		+= " BASE_YEAR="  + quote(ds_DcvgRst.GetColumn(ds_DcvgRst.row, "BASE_YEAR" ));
		strArgument		+= " MPIPE_ID="  + quote(ds_DcvgRst.GetColumn(ds_DcvgRst.row, "MPIPE_ID" ));
		strArgument		+= " EXPLOR_RSLT_SEQ="  + quote(ds_DcvgRst.GetColumn(ds_DcvgRst.row, "EXPLOR_RSLT_SEQ" ));
		

	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );	
			   	 	 
	
	edtFileList.text = "";

	if(ds_DcvgDamage.rowcount() == 0)
	{
		var row = ds_DcvgDamage.AddRow();
		ds_DcvgDamage.UpdateControl = false;
		ds_DcvgDamage.SetRowType(row, "insert");
		ds_DcvgDamage.SetColumn(row, "BASE_YEAR"    , ds_DcvgRst.GetColumn(ds_DcvgRst.row, "BASE_YEAR"));
		ds_DcvgDamage.SetColumn(row, "MPIPE_ID"     , ds_DcvgRst.GetColumn(ds_DcvgRst.row, "MPIPE_ID"));
		ds_DcvgDamage.SetColumn(row, "EXPLOR_RSLT_SEQ"     , ds_DcvgRst.GetColumn(ds_DcvgRst.row, "EXPLOR_RSLT_SEQ"));
		ds_DcvgDamage.SetColumn(row, "EXPLOR_YMD"     , ds_DcvgRst.GetColumn(ds_DcvgRst.row, "EXPLOR_YMD"));
		ds_DcvgDamage.SetColumn(row, "EXPLOR_BLOCK_SEQ"     , ds_DcvgRst.GetColumn(ds_DcvgRst.row, "EXPLOR_BLOCK_SEQ"));
		ds_DcvgDamage.SetColumn(row, "ZIP_NO1"   , ds_DcvgRst.GetColumn(ds_DcvgRst.row, "ZIP_NO1"));
		ds_DcvgDamage.SetColumn(row, "ZIP_NO2"   , ds_DcvgRst.GetColumn(ds_DcvgRst.row, "ZIP_NO2"));
		ds_DcvgDamage.SetColumn(row, "DAMAGE_ADDR"   , ds_DcvgRst.GetColumn(ds_DcvgRst.row, "DAMAGE_ADDR"));
		ds_DcvgDamage.SetColumn(row, "DAMAGE_DETAIL_ADDR"   , ds_DcvgRst.GetColumn(ds_DcvgRst.row, "DAMAGE_DETAIL_ADDR"));		
	}		   	
	else
	{
		fn_d1_FileSearch(nRow);
		fn_D1_FileListNmImg(nRow);
	}
	
	
}
function Grid2_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
		//-------------------------------------------------------------------------------
	//	sorting - Master Grid
	//-------------------------------------------------------------------------------
	gfn_SetGridSort(obj, nCell);
}


//---------------------------------------
//  그리드에 코드LOV 처리를 위한 함수 
//  Parameter : Input : Grid ID, Col No, Code_ID
//  예) gfn_SetGrdComboBox_Comcode("grd_master",1,"E20079");
//---------------------------------------
function SetGrdComboBox (objGrid,colNo,strCodeGrp) 
{
		var grdCodeGrp = gfn_setDsObject_GrdCode();

		eval(objGrid).SetCellProp("body",colNo,"display","combo");
		eval(objGrid).SetCellProp("body",colNo,"combodataset",eval(grdCodeGrp).id);
		eval(objGrid).SetCellProp("body",colNo,"combocol","CODE_ITEM");
		eval(objGrid).SetCellProp("body",colNo,"combotext","ITEM_KNAME");
		eval(objGrid).SetCellProp("body",colNo,"edit","none");
		
		gfn_SetComboBox_Comcode(strCodeGrp,grdCodeGrp);
		
		return true;
}
