function fn_D1_addRow()
{
	alert("조회 버튼을 클릭 후 저장/수정하세요...");
}


function fn_D1_deleteRow(){
	if(ds_EcdaDiary.GetRowType(ds_EcdaDiary.row) == "insert"){
		ds_EcdaDiary.DeleteRow(ds_EcdaDiary.row);
		return;
	}

	if(ds_EcdaDiary.GetRowCount() == 0)
	{
		return;
	}

	if((ds_EcdaDiary.GetColumn(0,"DECIDE_PROC_STS") == '0') 
	  || (ds_EcdaDiary.GetColumn(0,"DECIDE_PROC_STS") == '1')
	  || (ds_EcdaDiary.GetColumn(0,"DECIDE_PROC_STS") == '2'))
	{  
		alert("결재건으로 삭제가 불가능합니다..");
		return;
	}	
		
	if(confirm("ECDA 탐사내역을 삭제하시겠습니까?"))
	{
		fn_DelDiary();
	}	
}

//-------------------------------------------------------------------------------
// 함수명: fn_CommonDeleteRow()
// 설명: 접수행을 삭제 한다.
// arguments	없음
// return  없음
//-------------------------------------------------------------------------------
function fn_DelDiary()
{	
	ds_EcdaDiary.DeleteRow(ds_EcdaDiary.row);

    var strSvcID        = "save_EcdaDiary";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeAMgr-service"
						;
												
	var strInDatasets   = "ds_EcdaDiary=ds_EcdaDiary:u";
	var strOutDatasets  = "";
	var strArgument     = "save_EcdaDiary";
	 	 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ErrorCode < 0)
	{
		return;
	}
	
	alert("정상 삭제 되었습니다.");
	
	fn_D1_find();
}
//------------------------------------------------------------------------------- 
//   함수명 : fn_D1_SAVE
//   설명  : 구간 실적 등록된 내용을 저장한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
//-------------------------------------------------------------------------------
//  Transition   : Service id
//  ServicePart   : Service part
//  Url          : Server url
//  InputDsList   : Input Dataset List
//  OutputDsList   : Output Dataset List
//  Argument   :  Argument
//-------------------------------------------------------------------------------
function fn_D1_save()
{			
	if (ds_EcdaDiary.RowCount() == 0)
	{
		return;
	}
	
	if((ds_EcdaDiary.GetColumn(0,"DECIDE_PROC_STS") == '0')
	  || (ds_EcdaDiary.GetColumn(0,"DECIDE_PROC_STS") == '1')	
	  || (ds_EcdaDiary.GetColumn(0,"DECIDE_PROC_STS") == '2'))
	{  
		alert("결재건으로 수정이 불가능합니다..");
		return;
	}
	
	if (ds_EcdaDiary.GetRowType(0) == 'insert' || ds_EcdaDiary.GetRowType(0) == 'update')
	{	
		if(ds_EcdaDiary.GetColumn(0, "RESECH_EMPID1") == '' || ds_EcdaDiary.GetColumn(0, "RESECH_EMPID1") == null)
		{
			alert("조회 후 등록하세요. ");
			return;
		}
		
		if(ds_EcdaDiary.GetColumn(0, "EXPLOR_DUTY_DESC") == '' || ds_EcdaDiary.GetColumn(0, "EXPLOR_DUTY_DESC") == null)
		{
			alert("업무내용을 입력하세요. ");
			return;
		}
		
		if (ds_EcdaDiary.GetRowType(0) == 'insert') 
		{
			ds_EcdaDiary.SetColumn(0,"UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//수정자사번
			ds_EcdaDiary.SetColumn(0,"UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//수정자 IP
			ds_EcdaDiary.SetColumn(0,"CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
			ds_EcdaDiary.SetColumn(0,"CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP
		}	
		else
		{	
			ds_EcdaDiary.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//수정자사번
			ds_EcdaDiary.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//수정자 IP
		}
	}
	
	var strSvcID         = "save_EcdaDiary";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeAMgr-service" ;	
	var strInDatasets    = "ds_EcdaDiary=ds_EcdaDiary:u";
	var strOutDatasets   = "";		
	var strArgument      = "save_EcdaDiary";

	gfn_syncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				);
			   
	if(ErrorCode<0)
	{
		return;
	}
				
	alert("저장/수정 되었습니다.");
	
	fn_D1_find();
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Find()
//   설명  :  ECDA일지 조회
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_find()
{	
	
	if(!gfn_B3_CheckRequired(this, 30, 31)) return;
	
	ds_EcdaDiary.ClearData();
	ds_EcdaTotal.ClearData();
	ds_EcdaArRst.ClearData();
	
	ds_Search.ClearData();
	var nRow = ds_Search.AddRow();
	//Search Parameter Setting
	//조회조건에 대한 정리 필요 - 현재 BASE_YEAR 을 제외한 탭외부의 조회조건은 무시
    //탭내부의 일자 , 지사 조회조건	
	
	ds_Search.SetColumn(nRow, "BASE_YEAR", sp_Year.Value);
	ds_Search.SetColumn(nRow, "BRANCH_CD", cob_BranchCd.Value);
	ds_Search.SetColumn(nRow, "EXPLOR_YMD",cal_ExplorYmd.Value );
	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url 
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------	
    var ServiceId     = "view_EcdaDiary";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName=D1_DCVGSechActualResultPlanPipeAMgr-service";
    var InputDsList   = "ds_Search=ds_Search";
    var OutputDsList  = "ds_EcdaDiary=ds_EcdaDiary ds_EcdaTotal=ds_EcdaTotal ds_EcdaArRst=ds_EcdaArRst ";
		OutputDsList += "ds_EcdaRstM=ds_EcdaRstM ds_EcdaRstRL=ds_EcdaRstRL ds_EcdaRst=ds_EcdaRst";
    var Argument      = "view_EcdaDiary";
	
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument); 
	
	if(ds_EcdaDiary.GetRowCount() < 1) 	//신규작성
	{
		ds_EcdaDiary.AddRow();
		ds_EcdaDiary.SetColumn(0,"BASE_YEAR",sp_Year.Value);
		ds_EcdaDiary.SetColumn(0,"EXPLOR_YMD",cal_ExplorYmd.Value);
		ds_EcdaDiary.SetColumn(0,"BRANCH_CD",cob_BranchCd.Value);
		ds_EcdaDiary.SetColumn(0,"RESECH_EMPID1",gfn_GetGlobalValue("G_USER_NO"));
		ds_EcdaDiary.SetColumn(0,"RESECH_NAME1",gfn_GetGlobalValue("G_NAME")); 
		
		fn_CompControl(true);
	}
	//else if(gfn_isNull(ds_EcdaDiary.GetColumn(ds_EcdaDiary.row,"DECIDE_PROC_STS"))){
	// 2013-07-12 김형수 요청취소(4), 반려(5)도 수정가능 할 수 있게 변경
	if((ds_EcdaDiary.GetColumn(0,"DECIDE_PROC_STS") == '4')
	  || (ds_EcdaDiary.GetColumn(0,"DECIDE_PROC_STS") == '5'))	// 4:요청취소, 5:반려
	{
		
		//fn_CompControl(true);
		fn_CompControl(true);
	}
	else{
		fn_CompControl(false);
	}
	
	Grid.SetFocus();
	
}

 
