function edt_SWORK_NUM_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	// 백스페이스 , DELETE키
	if( nChar == 8 || nChar == 46)
	{
		edt_SWORK_NUM.Text = "";
	}
}

