		var strURL         = "handle.do?ServiceName="+fv_ServiceId+"-service";
		var strInDatasets  = "ds_CallParams_exam=ds_CallParams_exam:u";
		var strOutDatasets = "ds_CallBpmOut=ds_CallBpmOut";
		var strArgument    = "gis_input=1";   
		var strCallBack    = "fn_D1_CallBack";
			
		gfn_AsyncCall( 
						 strSvcID
					   , strPart
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
					   , strCallBack
				   );
	}
}

//--------------------------------------------------------------------------------------------------
//	3. GIS_오류통보
//--------------------------------------------------------------------------------------------------
function btn_GIS_RETURN_OnClick(obj)
{

	if(ds_JunResult.GetRowCount() <= 0) return;
	
	
	var str_WORK_NUM     = ds_JunList.GetColumn(ds_JunList.row, "WORK_NUM"); // 공사번호
	var str_SERIAL_NUM   = ds_JunResult.Max("SERIAL_NUM");
	var str_EMPID        = gfn_GetGlobalValue("G_USER_NO");
	var str_UPD_IP       = gfn_GetLocalIpAddress();
	var str_REG_YMD      = today();	
	
	if( gfn_isNull(str_WORK_NUM) )
	{
		alert("공사번호가 등록되지 않았습니다.");
		return false;
	}
	
	if( gfn_isNull(str_SERIAL_NUM) )
	{
		alert("차수가 등록되지 않았습니다.");
		return false;
	}
	
	if( gfn_isNull(str_EMPID) )
	{
		alert("[필수]사용자정보가 등록되지 않았습니다.");
		return false;
	}						

	ds_CallParams_exam.ClearData();
	ds_CallParams_exam.AddRow();
	/*
	ds_CallParams_exam.SetColumn(0,"userId"         , str_EMPID); // 사용자ID
	ds_CallParams_exam.SetColumn(0,"interfaceType"  , "Type4");
	ds_CallParams_exam.SetColumn(0,"processDefName" , "SS_CMI");      // /SS_CMI
	ds_CallParams_exam.SetColumn(0,"activityName"   , "CMIGISInput"); //CMIGISInput
	
	ds_CallParams_exam.SetColumn(0,"workNumPK"      , str_WORK_NUM); //공사번호
	ds_CallParams_exam.SetColumn(0,"gisInputYmd"    , str_REG_YMD);  //GIS입력일자
	ds_CallParams_exam.SetColumn(0,"errFlag"        , "Y");          //오류여부 (Y/N)
	ds_CallParams_exam.SetColumn(0,"errDesc"        , "오류내역");   //오류내역
	*/
	//  BPM항목 외 
	ds_CallParams_exam.SetColumn(0,"APPROVAL_TYPE"  , "TYPE_60");      // 구분 GIS오류통보(TYPE_60)
	ds_CallParams_exam.SetColumn(0,"WORK_NUM"       , str_WORK_NUM);   // 공사번호
	ds_CallParams_exam.SetColumn(0,"SERIAL_NUM"     , str_SERIAL_NUM); // 차수 
	ds_CallParams_exam.SetColumn(0,"REG_YMD"        , str_REG_YMD);    // 접수일자
	ds_CallParams_exam.SetColumn(0,"UPD_EMPID"      , str_EMPID);      //
	ds_CallParams_exam.SetColumn(0,"UPD_IP"         , str_UPD_IP);     //	
	
	fv_FindRow_ShowRow = 1;
	fv_FindRow_ShowKey = str_WORK_NUM;     //공사번호	
		
	var strSvcID       = "gis_input_return";
	var strPart        = "D10";
	var strURL         = "handle.do?ServiceName="+fv_ServiceId+"-service";
	var strInDatasets  = "ds_CallParams_exam=ds_CallParams_exam:u";
	var strOutDatasets = "ds_CallBpmOut=ds_CallBpmOut";
	var strArgument    = "gis_input=1";   
	var strCallBack    = "fn_D1_CallBack";
		
	gfn_AsyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				   , strCallBack
			   );
}

//--------------------------------------------------------------------------------------------------
//	GIS입력결과 조회
//--------------------------------------------------------------------------------------------------
function fn_D1_GisUnsuitResult()
{	
	if(confirm("GIS오류 통보를 하시겠습니까?"))
    {
		if(ds_JunResult.GetRowCount() <= 0) return;
	
		var str_WORK_NUM     = ds_JunList.GetColumn(ds_JunList.row, "WORK_NUM"); // 공사번호
		var str_SERIAL_NUM   = ds_JunResult.Max("SERIAL_NUM");
		
		ds_Unsuit_Result.ClearData();
		
		var strSvcID       = "UnsuitResult";
		var strPart        = "D10";
		var strURL         = "handle.do?ServiceName="+fv_ServiceId_List+"-service";
		var strInDatasets  = "";
		var strOutDatasets = "ds_Unsuit_Result=ds_Unsuit_Result";
		var strArgument    = "UnsuitResult=1";   
			strArgument   += " WORK_NUM="+ quote(str_WORK_NUM) ;
			strArgument   += " SERIAL_NUM="+ quote(str_SERIAL_NUM) ;
		var strCallBack    = "fn_D1_CallBack";
			
		gfn_AsyncCall( 
						 strSvcID
					   , strPart
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
					   , strCallBack
				   );
	}
}

// 메일 발송 버튼 클릭시
function btn_SendMail_OnClick(obj)
{
	for(i = 0; i < 2; i++)
	{
		var fv_WORK_NUM   = ds_JunList.GetColumn(ds_JunList.row,"WORK_NUM");
		var fv_WORK_NM = ds_JunList.GetColumn(ds_JunList.row,"WORK_NM");
		var fv_REVIEW_RSLT_EMPNM = ds_JunResult.GetColumn(ds_JunResult.row, "GIS_INPUT_EMPNM");
		//검수한사람의 사번을 보낸다 (검수한사람의 이메일로 보내기 위해서)
			
		var strSendAdd = ds_JunResult.GetColumn(ds_JunResult.row, "GIS_INPUT_EMPID");
		var strToAdd = "";

		if(i == 0)
		{
			strToAdd = ds_JunResult.GetColumn(ds_JunResult.row,"EMAIL_ADDR1");		
		}else{
			strToAdd = ds_JunResult.GetColumn(ds_JunResult.row,"REVIEW_EMAIL_ADDR");		
		}

		var strCCAdd = "";
		var strTitle = "준공도 오류통보 메일입니다";
		var strContents  = "";
		
		if(i == 0)
		{	
			strContents  = "서울도시가스 "   +quote(fv_REVIEW_RSLT_EMPNM) 
						 + "입니다."
						 + "\n" + "공사번호 "+quote(fv_WORK_NUM)
						 + " 공사명"+quote(fv_WORK_NM)
						 + " 준공도가 오류 통보되었으니 빠른시일내 협력사 수정 후 재등록 하도록 요청바랍니다.";
		}else{
			strContents  = "서울도시가스 "   +quote(fv_REVIEW_RSLT_EMPNM) 
						 + "입니다."
						 + "\n" + "공사번호 "+quote(fv_WORK_NUM)
						 + " 공사명"+quote(fv_WORK_NM)
						 + " 준공도가 오류 통보되었으니 빠른시일내 수정 후 재등록 하시기 바랍니다.";
		}
				 
				 
		if(i == 0)
		{	
			if(strToAdd == "") {
				alert("오류통보결과를 보낼 시공사의 E-MAIL주소가 없습니다. 담당자에게 연락해 주시기 바랍니다.");
				return false;
			}
			
			if(!gfn_isValidEmail(strToAdd)){
				alert("오류통보결과를 보낼 시공사의 E-MAIL주소 형태가 올바르지 않습니다. 담당자에게 연락해 주시기 바랍니다.");
				return false;
			}
		}else{
			if(strToAdd == "") {
				alert("오류통보결과를 보낼 공사감독의 E-MAIL주소가 없습니다. 담당자에게 연락해 주시기 바랍니다.");
				return false;
			}
			
			if(!gfn_isValidEmail(strToAdd)){
				alert("오류통보결과를 보낼 공사감독의 E-MAIL주소 형태가 올바르지 않습니다. 담당자에게 연락해 주시기 바랍니다.");
				return false;
			}	
		}
		
		// 처리내용 저장 성공시 메일발송
		gfn_SendMailOutside(strSendAdd, strToAdd, strCCAdd, strTitle, strContents);
		
		if (errorcode < 0)
		{
			alert("메일 발송에 실패하였습니다.");
			return;
		}	
		
		if(i == 0)
		{	
			alert("시공사에게 메일 발송에 성공하였습니다.");
		}else{
			alert("공사감독에게 메일 발송에 성공하였습니다.");
		}		
		
		
	}
}

